const childProcess = require('child_process');
const commander = require('../');
const path = require('path');
const util = require('util');

const execFileAsync = util.promisify(childProcess.execFile);

// Combination of parse and parseOptions tests which are more about details
// than high level behaviours which are tested elsewhere.

// Tests created from reported bugs
describe('regression tests', () => {
  // https://github.com/tj/commander.js/issues/1032
  function createProgram1032() {
    const program = new commander.Command();
    program
      .command('doit [id]')
      .option('--better', 'do it better')
      .action((id, cmd) => {});
    return program;
  }

  // https://github.com/tj/commander.js/issues/1032
  test('when specify subcommand and argument then program.args not empty', () => {
    const program = createProgram1032();
    program.parse(['node', 'test.js', 'doit', 'myid']);
    expect(program.args.length).toBeGreaterThan(0);
  });

  // https://github.com/tj/commander.js/issues/1032
  test('when specify subcommand and option and argument then program.args not empty', () => {
    const program = createProgram1032();
    program.parse(['node', 'test.js', 'doit', '--better', 'myid']);
    expect(program.args.length).toBeGreaterThan(0);
  });

  // https://github.com/tj/commander.js/issues/508
  test('when arguments to executable include option flags then argument order preserved', async () => {
    const pm = path.join(__dirname, 'fixtures/pm');
    const { stdout } = await execFileAsync('node', [
      pm,
      'echo',
      '1',
      '2',
      '--dry-run',
      '3',
      '4',
      '5',
      '6',
    ]);
    expect(stdout).toBe("[ '1', '2', '--dry-run', '3', '4', '5', '6' ]\n");
  });
});

describe('parseOptions', () => {
  function createProgram() {
    const program = new commander.Command();
    program
      .option('--global-flag')
      .option('--global-value <value>')
      .command('sub [args...]')
      .option('--sub-flag');
    program.action(() => {});
    return program;
  }

  // Subcommands are just another potential operand as far as parseOptions is concerned, so limited testing of subcommand as such.

  test('when empty args then empty results', () => {
    const program = createProgram();
    const result = program.parseOptions([]);
    expect(result).toEqual({ operands: [], unknown: [] });
  });

  test('when only operands then results has all operands', () => {
    const program = createProgram();
    const result = program.parseOptions('one two three'.split(' '));
    expect(result).toEqual({ operands: ['one', 'two', 'three'], unknown: [] });
  });

  test('when subcommand and operand then results has subcommand and operand', () => {
    const program = createProgram();
    const result = program.parseOptions('sub one'.split(' '));
    expect(result).toEqual({ operands: ['sub', 'one'], unknown: [] });
  });

  test('when program has flag then option removed', () => {
    const program = createProgram();
    const result = program.parseOptions('--global-flag'.split(' '));
    expect(result).toEqual({ operands: [], unknown: [] });
  });

  test('when program has option with value then option removed', () => {
    const program = createProgram();
    const result = program.parseOptions('--global-value foo'.split(' '));
    expect(result).toEqual({ operands: [], unknown: [] });
  });

  test('when program has flag before operand then option removed', () => {
    const program = createProgram();
    const result = program.parseOptions('--global-flag arg'.split(' '));
    expect(result).toEqual({ operands: ['arg'], unknown: [] });
  });

  test('when program has flag after operand then option removed', () => {
    const program = createProgram();
    const result = program.parseOptions('arg --global-flag'.split(' '));
    expect(result).toEqual({ operands: ['arg'], unknown: [] });
  });

  test('when program has flag after subcommand then option removed', () => {
    const program = createProgram();
    const result = program.parseOptions('sub --global-flag'.split(' '));
    expect(result).toEqual({ operands: ['sub'], unknown: [] });
  });

  test('when program has unknown option then option returned in unknown', () => {
    const program = createProgram();
    const result = program.parseOptions('--unknown'.split(' '));
    expect(result).toEqual({ operands: [], unknown: ['--unknown'] });
  });

  test('when program has unknown option before operands then all unknown in same order', () => {
    const program = createProgram();
    const result = program.parseOptions('--unknown arg'.split(' '));
    expect(result).toEqual({ operands: [], unknown: ['--unknown', 'arg'] });
  });

  test('when program has unknown option after operand then option returned in unknown', () => {
    const program = createProgram();
    const result = program.parseOptions('arg --unknown'.split(' '));
    expect(result).toEqual({ operands: ['arg'], unknown: ['--unknown'] });
  });

  test('when program has flag after unknown option then flag removed', () => {
    const program = createProgram();
    const result = program.parseOptions('--unknown --global-flag'.split(' '));
    expect(result).toEqual({ operands: [], unknown: ['--unknown'] });
  });

  test('when subcommand has flag then flag returned as unknown', () => {
    const program = createProgram();
    const result = program.parseOptions('sub --sub-flag'.split(' '));
    expect(result).toEqual({ operands: ['sub'], unknown: ['--sub-flag'] });
  });

  test('when program has literal before known flag then option returned as operand', () => {
    const program = createProgram();
    const result = program.parseOptions('-- --global-flag'.split(' '));
    expect(result).toEqual({ operands: ['--global-flag'], unknown: [] });
  });

  test('when program has literal before unknown option then option returned as operand', () => {
    const program = createProgram();
    const result = program.parseOptions('-- --unknown uuu'.split(' '));
    expect(result).toEqual({ operands: ['--unknown', 'uuu'], unknown: [] });
  });

  test('when program has literal after unknown option then literal preserved too', () => {
    const program = createProgram();
    const result = program.parseOptions('--unknown1 -- --unknown2'.split(' '));
    expect(result).toEqual({
      operands: [],
      unknown: ['--unknown1', '--', '--unknown2'],
    });
  });
});

// parse now sets program.args to the result of parseOptions (operands + unknown). Some limited testing.
describe('parse and program.args', () => {
  test('when program has known flag and operand then option removed and operand returned', () => {
    const program = new commander.Command();
    program.option('--global-flag').argument('[arg...]');
    program.parse('node test.js --global-flag arg'.split(' '));
    expect(program.args).toEqual(['arg']);
  });

  test('when program has mixed arguments then known options removed and rest returned in same order', () => {
    const program = new commander.Command();
    program
      .allowUnknownOption()
      .option('--global-flag')
      .option('--global-value <value>')
      .argument('[arg...]');
    program.parse(
      'node test.js aaa --global-flag bbb --unknown ccc --global-value value'.split(
        ' ',
      ),
    );
    expect(program.args).toEqual(['aaa', 'bbb', '--unknown', 'ccc']);
  });

  test('when subcommand has mixed arguments then program flags removed and rest returned in same order', () => {
    const program = new commander.Command();
    program
      .option('--global-flag')
      .option('--global-value <value>')
      .command('sub [args...]')
      .option('--sub-flag');
    program.parse(
      'node test.js --global-flag sub --sub-flag arg --global-value value'.split(
        ' ',
      ),
    );
    expect(program.args).toEqual(['sub', '--sub-flag', 'arg']);
  });
});

// Utility Conventions: https://pubs.opengroup.org/onlinepubs/9699919799/basedefs/V1_chap12.html#tag_12_01
//
// 12.1 Utility Argument Syntax
// 2. Option-arguments are shown separated from their options by <blank> characters, except when the option-argument is
// enclosed in the '[' and ']' notation to indicate that it is optional. This reflects the situation in which an
// optional option-argument (if present) is included within the same argument string as the option; for a mandatory option-argument,
// it is the next argument. The Utility Syntax Guidelines in Utility Syntax Guidelines require that the option be a separate argument
// from its option-argument and that option-arguments not be optional, but there are some exceptions in POSIX.1-2017 to ensure
// continued operation of historical applications:
//
// a. If the SYNOPSIS of a standard utility shows an option with a mandatory option-argument (as with [ -c option_argument] in the example),
//    a conforming application shall use separate arguments for that option and its option-argument. However, a conforming implementation shall
//    also permit applications to specify the option and option-argument in the same argument string without intervening <blank> characters.
//
// b. If the SYNOPSIS shows an optional option-argument (as with [ -f[ option_argument]] in the example), a conforming application
//    shall place any option-argument for that option directly adjacent to the option in the same argument string, without intervening
//    <blank> characters. If the utility receives an argument containing only the option, it shall behave as specified in its description
//    for an omitted option-argument; it shall not treat the next argument (if any) as the option-argument for that option.
//
// 12.2 Utility Syntax Guidelines, Guideline 5:
// One or more options without option-arguments, followed by at most one option that takes an option-argument, should be accepted when
// grouped behind one '-' delimiter.

// Commander conformance:
// - allows separate argument for required option-argument
// - allows value in same argument for short flag with required option-argument
// - non-conforming: allows separate argument for optional option-argument if it does not start with '-'
// - allows value in same argument for short flag with optional option-argument
// - allows short flags as per 12.2
//
// The focus in this file is testing the behaviours with known vs unknown options.
// See options.values.test.js for more general testing of known options.

describe('Utility Conventions', () => {
  function createProgram() {
    const program = new commander.Command();
    program
      .option('-a,--aaa')
      .option('-b,--bbb')
      .option('-c,--ccc <value>')
      .option('-d,--ddd [value]');
    program.action(() => {});
    return program;
  }

  test('when program has combo known boolean short flags then arg removed', () => {
    const program = createProgram();
    const result = program.parseOptions(['-ab']);
    expect(result).toEqual({ operands: [], unknown: [] });
    expect(program.opts()).toEqual({ aaa: true, bbb: true });
  });

  test('when program has combo unknown short flags then arg preserved', () => {
    const program = createProgram();
    const result = program.parseOptions(['-pq']);
    expect(result).toEqual({ operands: [], unknown: ['-pq'] });
    expect(program.opts()).toEqual({});
  });

  test('when program has combo known short option and required value then arg removed', () => {
    const program = createProgram();
    const result = program.parseOptions(['-cvalue']);
    expect(result).toEqual({ operands: [], unknown: [] });
    expect(program.opts()).toEqual({ ccc: 'value' });
  });

  test('when program has combo known short option and optional value then arg removed', () => {
    const program = createProgram();
    const result = program.parseOptions(['-dvalue']);
    expect(result).toEqual({ operands: [], unknown: [] });
    expect(program.opts()).toEqual({ ddd: 'value' });
  });

  test('when program has known combo short boolean flags and required value then arg removed', () => {
    const program = createProgram();
    const result = program.parseOptions(['-abcvalue']);
    expect(result).toEqual({ operands: [], unknown: [] });
    expect(program.opts()).toEqual({ aaa: true, bbb: true, ccc: 'value' });
  });

  test('when program has known combo short boolean flags and optional value then arg removed', () => {
    const program = createProgram();
    const result = program.parseOptions(['-abdvalue']);
    expect(result).toEqual({ operands: [], unknown: [] });
    expect(program.opts()).toEqual({ aaa: true, bbb: true, ddd: 'value' });
  });

  test('when program has known long flag=value then arg removed', () => {
    const program = createProgram();
    const result = program.parseOptions(['--ccc=value']);
    expect(result).toEqual({ operands: [], unknown: [] });
    expect(program.opts()).toEqual({ ccc: 'value' });
  });

  test('when program has unknown long flag=value then arg preserved', () => {
    const program = createProgram();
    const result = program.parseOptions(['--rrr=value']);
    expect(result).toEqual({ operands: [], unknown: ['--rrr=value'] });
    expect(program.opts()).toEqual({});
  });

  test('when program has combo optional and combineFlagAndOptionalValue() then treat as value', () => {
    const program = createProgram();
    program.combineFlagAndOptionalValue();
    program.parseOptions(['-db']);
    expect(program.opts()).toEqual({ ddd: 'b' });
  });

  test('when program has combo optional and combineFlagAndOptionalValue(true) then treat as value', () => {
    const program = createProgram();
    program.combineFlagAndOptionalValue(true);
    program.parseOptions(['-db']);
    expect(program.opts()).toEqual({ ddd: 'b' });
  });

  test('when program has combo optional and combineFlagAndOptionalValue(false) then treat as boolean', () => {
    const program = createProgram();
    program.combineFlagAndOptionalValue(false);
    program.parseOptions(['-db']);
    expect(program.opts()).toEqual({ ddd: true, bbb: true });
  });
});

tests/options.env.test.js.source:
const commander = require('../');

// treating optional same as required, treat as option taking value rather than as boolean
describe.each(['-f, --foo <required-arg>', '-f, --foo [optional-arg]'])(
  'option declared as: %s',
  (fooFlags) => {
    test('when env undefined and no cli then option undefined', () => {
      const program = new commander.Command();
      program.addOption(new commander.Option(fooFlags).env('BAR'));
      program.parse([], { from: 'user' });
      expect(program.opts().foo).toBeUndefined();
    });

    test('when env defined and no cli then option from env', () => {
      const program = new commander.Command();
      process.env.BAR = 'env';
      program.addOption(new commander.Option(fooFlags).env('BAR'));
      program.parse([], { from: 'user' });
      expect(program.opts().foo).toBe('env');
      delete process.env.BAR;
    });

    test('when env defined and cli then option from cli', () => {
      const program = new commander.Command();
      process.env.BAR = 'env';
      program.addOption(new commander.Option(fooFlags).env('BAR'));
      program.parse(['--foo', 'cli'], { from: 'user' });
      expect(program.opts().foo).toBe('cli');
      delete process.env.BAR;
    });

    test('when env defined and value source is config then option from env', () => {
      const program = new commander.Command();
      process.env.BAR = 'env';
      program.addOption(new commander.Option(fooFlags).env('BAR'));
      program.setOptionValueWithSource('foo', 'config', 'config');
      program.parse([], { from: 'user' });
      expect(program.opts().foo).toBe('env');
      delete process.env.BAR;
    });

    test('when env defined and value source is unspecified then option unchanged', () => {
      const program = new commander.Command();
      process.env.BAR = 'env';
      program.addOption(new commander.Option(fooFlags).env('BAR'));
      program.setOptionValue('foo', 'client');
      program.parse([], { from: 'user' });
      expect(program.opts().foo).toBe('client');
      delete process.env.BAR;
    });

    test('when default and env undefined and no cli then option from default', () => {
      const program = new commander.Command();
      program.addOption(
        new commander.Option(fooFlags).env('BAR').default('default'),
      );
      program.parse([], { from: 'user' });
      expect(program.opts().foo).toBe('default');
    });

    test('when default and env defined and no cli then option from env', () => {
      const program = new commander.Command();
      process.env.BAR = 'env';
      program.addOption(
        new commander.Option(fooFlags).env('BAR').default('default'),
      );
      program.parse([], { from: 'user' });
      expect(program.opts().foo).toBe('env');
      delete process.env.BAR;
    });

    test('when default and env defined and cli then option from cli', () => {
      const program = new commander.Command();
      process.env.BAR = 'env';
      program.addOption(
        new commander.Option(fooFlags).env('BAR').default('default'),
      );
      program.parse(['--foo', 'cli'], { from: 'user' });
      expect(program.opts().foo).toBe('cli');
      delete process.env.BAR;
    });
  },
);

describe('boolean flag', () => {
  test('when env undefined and no cli then option undefined', () => {
    const program = new commander.Command();
    program.addOption(new commander.Option('-f, --foo').env('BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBeUndefined();
  });

  test('when env defined with value and no cli then option true', () => {
    const program = new commander.Command();
    process.env.BAR = 'env';
    program.addOption(new commander.Option('-f, --foo').env('BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(true);
    delete process.env.BAR;
  });

  test('when env is "" and no cli then option true', () => {
    // any string, including ""
    const program = new commander.Command();
    process.env.BAR = '';
    program.addOption(new commander.Option('-f, --foo').env('BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(true);
    delete process.env.BAR;
  });

  test('when env is "0" and no cli then option true', () => {
    // any string, including "0"
    const program = new commander.Command();
    process.env.BAR = '0';
    program.addOption(new commander.Option('-f, --foo').env('BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(true);
    delete process.env.BAR;
  });

  test('when env is "false" and no cli then option true', () => {
    // any string, including "false"
    const program = new commander.Command();
    process.env.BAR = 'false';
    program.addOption(new commander.Option('-f, --foo').env('BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(true);
    delete process.env.BAR;
  });
});

describe('boolean no-flag', () => {
  test('when env undefined and no cli then option true', () => {
    const program = new commander.Command();
    program.addOption(new commander.Option('-F, --no-foo').env('NO_BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(true);
  });

  test('when env defined and no cli then option false', () => {
    const program = new commander.Command();
    process.env.NO_BAR = 'env';
    program.addOption(new commander.Option('-F, --no-foo').env('NO_BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(false);
    delete process.env.NO_BAR;
  });
});

describe('boolean flag and negatable', () => {
  test('when env undefined and no cli then option undefined', () => {
    const program = new commander.Command();
    program
      .addOption(new commander.Option('-f, --foo').env('BAR'))
      .addOption(new commander.Option('-F, --no-foo').env('NO_BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBeUndefined();
  });

  test('when env defined and no cli then option true', () => {
    const program = new commander.Command();
    process.env.BAR = 'env';
    program
      .addOption(new commander.Option('-f, --foo').env('BAR'))
      .addOption(new commander.Option('-F, --no-foo').env('NO_BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(true);
    delete process.env.BAR;
  });

  test('when env defined and cli --no-foo then option false', () => {
    const program = new commander.Command();
    process.env.BAR = 'env';
    program
      .addOption(new commander.Option('-f, --foo').env('BAR'))
      .addOption(new commander.Option('-F, --no-foo').env('NO_BAR'));
    program.parse(['--no-foo'], { from: 'user' });
    expect(program.opts().foo).toBe(false);
    delete process.env.BAR;
  });

  test('when no_env defined and no cli then option false', () => {
    const program = new commander.Command();
    process.env.NO_BAR = 'env';
    program
      .addOption(new commander.Option('-f, --foo').env('BAR'))
      .addOption(new commander.Option('-F, --no-foo').env('NO_BAR'));
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe(false);
    delete process.env.NO_BAR;
  });

  test('when no_env defined and cli --foo then option true', () => {
    const program = new commander.Command();
    process.env.NO_BAR = 'env';
    program
      .addOption(new commander.Option('-f, --foo').env('BAR'))
      .addOption(new commander.Option('-F, --no-foo').env('NO_BAR'));
    program.parse(['--foo'], { from: 'user' });
    expect(program.opts().foo).toBe(true);
    delete process.env.NO_BAR;
  });
});

describe('custom argParser', () => {
  test('when env defined and no cli then custom parse from env', () => {
    const program = new commander.Command();
    process.env.BAR = 'env';
    program.addOption(
      new commander.Option('-f, --foo <required>')
        .env('BAR')
        .argParser((str) => str.toUpperCase()),
    );
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toBe('ENV');
    delete process.env.BAR;
  });
});

describe('variadic', () => {
  test('when env defined and no cli then array from env', () => {
    const program = new commander.Command();
    process.env.BAR = 'env';
    program.addOption(
      new commander.Option('-f, --foo <required...>').env('BAR'),
    );
    program.parse([], { from: 'user' });
    expect(program.opts().foo).toEqual(['env']);
    delete process.env.BAR;
  });

  test('when env defined and cli then array from cli', () => {
    const program = new commander.Command();
    process.env.BAR = 'env';
    program.addOption(
      new commander.Option('-f, --foo <required...>').env('BAR'),
    );
    program.parse(['--foo', 'cli'], { from: 'user' });
    expect(program.opts().foo).toEqual(['cli']);
    delete process.env.BAR;
  });
});

describe('env only processed when applies', () => {
  test('when env defined on another subcommand then env not applied', () => {
    // Doing selective processing. Not processing env at addOption time.
    const program = new commander.Command();
    process.env.BAR = 'env';
    program.command('one').action(() => {});
    const two = program
      .command('two')
      .addOption(
        new commander.Option('-f, --foo <required...>')
          .env('BAR')
          .default('default'),
      )
      .action(() => {});
    program.parse(['one'], { from: 'user' });
    expect(two.opts().foo).toBe('default');
    delete process.env.BAR;
  });

  test('when env and cli defined then only emit option event for cli', () => {
    const program = new commander.Command();
    const optionEventMock = jest.fn();
    const optionEnvEventMock = jest.fn();
    program.on('option:foo', optionEventMock);
    program.on('optionEnv:foo', optionEnvEventMock);
    process.env.BAR = 'env';
    program.addOption(
      new commander.Option('-f, --foo <required...>').env('BAR'),
    );
    program.parse(['--foo', 'cli'], { from: 'user' });
    expect(optionEventMock).toHaveBeenCalledWith('cli');
    expect(optionEventMock).toHaveBeenCalledTimes(1);
    expect(optionEnvEventMock).toHaveBeenCalledTimes(0);
    delete process.env.BAR;
  });

  test('when env and cli defined then only parse value for cli', () => {
    const program = new commander.Command();
    const parseMock = jest.fn();
    process.env.BAR = 'env';
    program.addOption(
      new commander.Option('-f, --foo <required...>')
        .env('BAR')
        .argParser(parseMock),
    );
    program.parse(['--foo', 'cli'], { from: 'user' });
    expect(parseMock).toHaveBeenCalledWith('cli', undefined);
    expect(parseMock).toHaveBeenCalledTimes(1);
    delete process.env.BAR;
  });
});

describe('events dispatched for env', () => {
  const optionEnvEventMock = jest.fn();

  afterEach(() => {
    optionEnvEventMock.mockClear();
    delete process.env.BAR;
  });

  test('when env defined then emit "optionEnv" and not "option"', () => {
    // Decided to do separate events, so test stays that way.
    const program = new commander.Command();
    const optionEventMock = jest.fn();
    program.on('option:foo', optionEventMock);
    program.on('optionEnv:foo', optionEnvEventMock);
    process.env.BAR = 'env';
    program.addOption(new commander.Option('-f, --foo <required>').env('BAR'));
    program.parse([], { from: 'user' });
    expect(optionEventMock).toHaveBeenCalledTimes(0);
    expect(optionEnvEventMock).toHaveBeenCalledTimes(1);
  });

  test('when env defined for required then emit "optionEnv" with value', () => {
    const program = new commander.Command();
    program.on('optionEnv:foo', optionEnvEventMock);
    process.env.BAR = 'env';
    program.addOption(new commander.Option('-f, --foo <required>').env('BAR'));
    program.parse([], { from: 'user' });
    expect(optionEnvEventMock).toHaveBeenCalledWith('env');
  });

  test('when env defined for optional then emit "optionEnv" with value', () => {
    const program = new commander.Command();
    program.on('optionEnv:foo', optionEnvEventMock);
    process.env.BAR = 'env';
    program.addOption(new commander.Option('-f, --foo [optional]').env('BAR'));
    program.parse([], { from: 'user' });
    expect(optionEnvEventMock).toHaveBeenCalledWith('env');
  });

  test('when env defined for boolean then emit "optionEnv" with no param', () => {
    // check matches historical boolean action event
    const program = new commander.Command();
    program.on('optionEnv:foo', optionEnvEventMock);
    process.env.BAR = 'anything';
    program.addOption(new commander.Option('-f, --foo').env('BAR'));
    program.parse([], { from: 'user' });
    expect(optionEnvEventMock).toHaveBeenCalledWith();
  });

  test('when env defined for negated boolean then emit "optionEnv" with no param', () => {
    // check matches historical boolean action event
    const program = new commander.Command();
    program.on('optionEnv:no-foo', optionEnvEventMock);
    process.env.BAR = 'anything';
    program.addOption(new commander.Option('-F, --no-foo').env('BAR'));
    program.parse([], { from: 'user' });
    expect(optionEnvEventMock).toHaveBeenCalledWith();
  });
});

tests/command.hook.test.js.source:
const commander = require('../');

test('when hook event wrong then throw', () => {
  const program = new commander.Command();
  expect(() => {
    program.hook('silly', () => {});
  }).toThrow();
});

test('when no action then action hooks not called', () => {
  const hook = jest.fn();
  const program = new commander.Command();
  program.hook('preAction', hook).hook('postAction', hook);
  program.parse([], { from: 'user' });
  expect(hook).not.toHaveBeenCalled();
});

describe('action hooks with synchronous hooks, order', () => {
  test('when hook preAction then hook called before action', () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', () => calls.push('before'))
      .action(() => calls.push('action'));
    program.parse([], { from: 'user' });
    expect(calls).toEqual(['before', 'action']);
  });

  test('when hook postAction then hook called after action', () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('postAction', () => calls.push('after'))
      .action(() => calls.push('action'));
    program.parse([], { from: 'user' });
    expect(calls).toEqual(['action', 'after']);
  });

  test('when hook preAction twice then hooks called FIFO', () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', () => calls.push('1'))
      .hook('preAction', () => calls.push('2'))
      .action(() => calls.push('action'));
    program.parse([], { from: 'user' });
    expect(calls).toEqual(['1', '2', 'action']);
  });

  test('when hook postAction twice then hooks called LIFO', () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('postAction', () => calls.push('1'))
      .hook('postAction', () => calls.push('2'))
      .action(() => calls.push('action'));
    program.parse([], { from: 'user' });
    expect(calls).toEqual(['action', '2', '1']);
  });

  test('when hook preAction at program and sub then hooks called program then sub', () => {
    const calls = [];
    const program = new commander.Command();
    program.hook('preAction', () => calls.push('program'));
    program
      .command('sub')
      .hook('preAction', () => calls.push('sub'))
      .action(() => calls.push('action'));
    program.parse(['sub'], { from: 'user' });
    expect(calls).toEqual(['program', 'sub', 'action']);
  });

  test('when hook postAction at program and sub then hooks called sub then program', () => {
    const calls = [];
    const program = new commander.Command();
    program.hook('postAction', () => calls.push('program'));
    program
      .command('sub')
      .hook('postAction', () => calls.push('sub'))
      .action(() => calls.push('action'));
    program.parse(['sub'], { from: 'user' });
    expect(calls).toEqual(['action', 'sub', 'program']);
  });

  test('when hook everything then hooks called nested', () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', () => calls.push('pb1'))
      .hook('postAction', () => calls.push('pa1'));
    program
      .hook('preAction', () => calls.push('pb2'))
      .hook('postAction', () => calls.push('pa2'));
    program
      .command('sub')
      .hook('preAction', () => calls.push('sb'))
      .hook('postAction', () => calls.push('sa'))
      .action(() => calls.push('action'));
    program.parse(['sub'], { from: 'user' });
    expect(calls).toEqual(['pb1', 'pb2', 'sb', 'action', 'sa', 'pa2', 'pa1']);
  });
});

describe('action hooks context', () => {
  test('when hook on program then passed program/program', () => {
    const hook = jest.fn();
    const program = new commander.Command();
    program.hook('preAction', hook).action(() => {});
    program.parse([], { from: 'user' });
    expect(hook).toHaveBeenCalledWith(program, program);
  });

  test('when hook on program and call sub then passed program/sub', () => {
    const hook = jest.fn();
    const program = new commander.Command();
    program.hook('preAction', hook);
    const sub = program.command('sub').action(() => {});
    program.parse(['sub'], { from: 'user' });
    expect(hook).toHaveBeenCalledWith(program, sub);
  });

  test('when hook on sub and call sub then passed sub/sub', () => {
    const hook = jest.fn();
    const program = new commander.Command();
    const sub = program
      .command('sub')
      .hook('preAction', hook)
      .action(() => {});
    program.parse(['sub'], { from: 'user' });
    expect(hook).toHaveBeenCalledWith(sub, sub);
  });

  test('when hook program on preAction then thisCommand has options set', () => {
    expect.assertions(1);
    const program = new commander.Command();
    program
      .option('--debug')
      .hook('preAction', (thisCommand) => {
        expect(thisCommand.opts().debug).toEqual(true);
      })
      .action(() => {});
    program.parse(['--debug'], { from: 'user' });
  });

  test('when hook program on preAction and call sub then thisCommand has program options set', () => {
    expect.assertions(1);
    const program = new commander.Command();
    program.option('--debug').hook('preAction', (thisCommand) => {
      expect(thisCommand.opts().debug).toEqual(true);
    });
    program.command('sub').action(() => {});
    program.parse(['sub', '--debug'], { from: 'user' });
  });

  test('when hook program on preAction and call sub then actionCommand has sub options set', () => {
    expect.assertions(1);
    const program = new commander.Command();
    program.hook('preAction', (thisCommand, actionCommand) => {
      expect(actionCommand.opts().debug).toEqual(true);
    });
    program
      .command('sub')
      .option('--debug')
      .action(() => {});
    program.parse(['sub', '--debug'], { from: 'user' });
  });

  test('when hook program on preAction then actionCommand has args set', () => {
    expect.assertions(1);
    const program = new commander.Command();
    program
      .argument('[arg]')
      .hook('preAction', (thisCommand, actionCommand) => {
        expect(actionCommand.args).toEqual(['value']);
      })
      .action(() => {});
    program.parse(['value'], { from: 'user' });
  });

  test('when hook program on preAction then actionCommand has args set with options removed', () => {
    expect.assertions(1);
    const program = new commander.Command();
    program
      .argument('[arg]')
      .option('--debug')
      .hook('preAction', (thisCommand, actionCommand) => {
        expect(actionCommand.args).toEqual(['value']);
      })
      .action(() => {});
    program.parse(['value', '--debug'], { from: 'user' });
  });

  test('when hook program on preAction and call sub then thisCommand has program args set', () => {
    expect.assertions(1);
    const program = new commander.Command();
    program.argument('[arg]').hook('preAction', (thisCommand) => {
      expect(thisCommand.args).toEqual(['sub', 'value']);
    });
    program
      .command('sub')
      .argument('<arg>')
      .action(() => {});
    program.parse(['sub', 'value'], { from: 'user' });
  });

  test('when hook program on preAction and call sub then actionCommand has sub args set', () => {
    expect.assertions(1);
    const program = new commander.Command();
    program.hook('preAction', (thisCommand, actionCommand) => {
      expect(actionCommand.args).toEqual(['value']);
    });
    program
      .command('sub')
      .argument('<arg>')
      .action(() => {});
    program.parse(['sub', 'value'], { from: 'user' });
  });
});

describe('action hooks async', () => {
  test('when async preAction then async from preAction', async () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', async () => {
        await 0;
        calls.push('before');
      })
      .action(() => calls.push('action'));
    const result = program.parseAsync([], { from: 'user' });
    expect(calls).toEqual([]);
    await result;
    expect(calls).toEqual(['before', 'action']);
  });

  test('when async postAction then async from postAction', async () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('postAction', async () => {
        await 0;
        calls.push('after');
      })
      .action(() => calls.push('action'));
    const result = program.parseAsync([], { from: 'user' });
    expect(calls).toEqual(['action']);
    await result;
    expect(calls).toEqual(['action', 'after']);
  });

  test('when async action then async from action', async () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', () => calls.push('before'))
      .hook('postAction', () => calls.push('after'))
      .action(async () => {
        await 0;
        calls.push('action');
      });
    const result = program.parseAsync([], { from: 'user' });
    expect(calls).toEqual(['before']);
    await result;
    expect(calls).toEqual(['before', 'action', 'after']);
  });

  test('when async first preAction then async from first preAction', async () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', async () => {
        await 0;
        calls.push('1');
      })
      .hook('preAction', () => calls.push('2'))
      .action(() => calls.push('action'));
    const result = program.parseAsync([], { from: 'user' });
    expect(calls).toEqual([]);
    await result;
    expect(calls).toEqual(['1', '2', 'action']);
  });

  test('when async second preAction then async from second preAction', async () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', () => calls.push('1'))
      .hook('preAction', async () => {
        await 0;
        calls.push('2');
      })
      .action(() => calls.push('action'));
    const result = program.parseAsync([], { from: 'user' });
    expect(calls).toEqual(['1']);
    await result;
    expect(calls).toEqual(['1', '2', 'action']);
  });

  test('when async hook everything then hooks called nested', async () => {
    const calls = [];
    const program = new commander.Command();
    program
      .hook('preAction', async () => {
        await 0;
        calls.push('pb1');
      })
      .hook('postAction', async () => {
        await 0;
        calls.push('pa1');
      });
    program
      .hook('preAction', async () => {
        await 0;
        calls.push('pb2');
      })
      .hook('postAction', async () => {
        await 0;
        calls.push('pa2');
      });
    program
      .command('sub')
      .hook('preAction', async () => {
        await 0;
        calls.push('sb');
      })
      .hook('postAction', async () => {
        await 0;
        calls.push('sa');
      })
      .action(async () => {
        await 0;
        calls.push('action');
      });
    const result = program.parseAsync(['sub'], { from: 'user' });
    expect(calls).toEqual([]);
    await result;
    expect(calls).toEqual(['pb1', 'pb2', 'sb', 'action', 'sa', 'pa2', 'pa1']);
  });

  test('preSubcommand hook should work', async () => {
    const calls = [];
    const program = new commander.Command();
    program.hook('preSubcommand', async () => {
      await 0;
      calls.push(0);
    });
    program.command('sub').action(async () => {
      await 1;
      calls.push(1);
    });
    program.action(async () => {
      await 2;
      calls.push(2);
    });
    const result = program.parseAsync(['sub'], { from: 'user' });
    expect(calls).toEqual([]);
    await result;
    expect(calls).toEqual([0, 1]);
  });
  test('preSubcommand hook should effective for direct subcommands', async () => {
    const calls = [];
    const program = new commander.Command();
    program.hook('preSubcommand', async (thisCommand, subCommand) => {
      await 'preSubcommand';
      calls.push('preSubcommand');
      calls.push(subCommand.name());
    });
    program
      .command('first')
      .action(async () => {
        await 'first';
        calls.push('first');
      })
      .command('second')
      .action(async () => {
        await 'second';
        calls.push('second');
      })
      .command('third')
      .action(async () => {
        await 'third';
        calls.push('third');
      });
    program.action(async () => {
      await 2;
      calls.push(2);
    });
    const result = program.parseAsync(['first', 'second', 'third'], {
      from: 'user',
    });
    expect(calls).toEqual([]);
    await result;
    expect(calls).toEqual(['preSubcommand', 'first', 'third']);
  });
});

tests/command.exitOverride.test.js.source:
const commander = require('../');
const path = require('path');

// Test details of the exitOverride errors.
// The important checks are the exitCode and code which are intended to be stable for
// semver minor versions. For now, also testing the error.message and that output occurred
// to detect accidental changes in behaviour.

/* eslint jest/expect-expect: ["error", { "assertFunctionNames": ["expect", "expectCommanderError"] }] */

function expectCommanderError(err, exitCode, code, message) {
  expect(err).toBeInstanceOf(commander.CommanderError);
  expect(err.exitCode).toBe(exitCode);
  expect(err.code).toBe(code);
  expect(err.message).toBe(message);
}

describe('.exitOverride and error details', () => {
  // Use internal knowledge to suppress output to keep test output clean.
  let stderrSpy;

  beforeAll(() => {
    stderrSpy = jest
      .spyOn(process.stderr, 'write')
      .mockImplementation(() => {});
  });

  afterEach(() => {
    stderrSpy.mockClear();
  });

  afterAll(() => {
    stderrSpy.mockRestore();
  });

  test('when specify unknown program option then throw CommanderError', () => {
    const program = new commander.Command();
    program.exitOverride();

    let caughtErr;
    try {
      program.parse(['node', 'test', '-m']);
    } catch (err) {
      caughtErr = err;
    }

    expect(stderrSpy).toHaveBeenCalled();
    expectCommanderError(
      caughtErr,
      1,
      'commander.unknownOption',
      "error: unknown option '-m'",
    );
  });

  test('when specify unknown command then throw CommanderError', () => {
    const program = new commander.Command();
    program.name('prog').exitOverride().command('sub');

    let caughtErr;
    try {
      program.parse(['node', 'test', 'oops']);
    } catch (err) {
      caughtErr = err;
    }

    expect(stderrSpy).toHaveBeenCalled();
    expectCommanderError(
      caughtErr,
      1,
      'commander.unknownCommand',
      "error: unknown command 'oops'",
    );
  });

  // Same error as above, but with custom handler.
  test('when supply custom handler then throw custom error', () => {
    const customError = new commander.CommanderError(
      123,
      'custom-code',
      'custom-message',
    );
    const program = new commander.Command();
    program.exitOverride((_err) => {
      throw customError;
    });

    let caughtErr;
    try {
      program.parse(['node', 'test', '-m']);
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      customError.exitCode,
      customError.code,
      customError.message,
    );
  });

  test('when specify option without required value then throw CommanderError', () => {
    const optionFlags = '-p, --pepper <type>';
    const program = new commander.Command();
    program.exitOverride().option(optionFlags, 'add pepper');

    let caughtErr;
    try {
      program.parse(['node', 'test', '--pepper']);
    } catch (err) {
      caughtErr = err;
    }

    expect(stderrSpy).toHaveBeenCalled();
    expectCommanderError(
      caughtErr,
      1,
      'commander.optionMissingArgument',
      `error: option '${optionFlags}' argument missing`,
    );
  });

  test('when specify command without required argument then throw CommanderError', () => {
    const program = new commander.Command();
    program
      .exitOverride()
      .command('compress <arg-name>')
      .action(() => {});

    let caughtErr;
    try {
      program.parse(['node', 'test', 'compress']);
    } catch (err) {
      caughtErr = err;
    }

    expect(stderrSpy).toHaveBeenCalled();
    expectCommanderError(
      caughtErr,
      1,
      'commander.missingArgument',
      "error: missing required argument 'arg-name'",
    );
  });

  test('when specify program without required argument and no action handler then throw CommanderError', () => {
    const program = new commander.Command();
    program.exitOverride().argument('<arg-name>');

    let caughtErr;
    try {
      program.parse(['node', 'test']);
    } catch (err) {
      caughtErr = err;
    }

    expect(stderrSpy).toHaveBeenCalled();
    expectCommanderError(
      caughtErr,
      1,
      'commander.missingArgument',
      "error: missing required argument 'arg-name'",
    );
  });

  test('when specify excess argument then throw CommanderError', () => {
    const program = new commander.Command();
    program
      .exitOverride()
      .allowExcessArguments(false)
      .action(() => {});

    let caughtErr;
    try {
      program.parse(['node', 'test', 'excess']);
    } catch (err) {
      caughtErr = err;
    }

    expect(stderrSpy).toHaveBeenCalled();
    expectCommanderError(
      caughtErr,
      1,
      'commander.excessArguments',
      'error: too many arguments. Expected 0 arguments but got 1.',
    );
  });

  test('when specify command with excess argument then throw CommanderError', () => {
    const program = new commander.Command();
    program
      .exitOverride()
      .command('speak')
      .allowExcessArguments(false)
      .action(() => {});

    let caughtErr;
    try {
      program.parse(['node', 'test', 'speak', 'excess']);
    } catch (err) {
      caughtErr = err;
    }

    expect(stderrSpy).toHaveBeenCalled();
    expectCommanderError(
      caughtErr,
      1,
      'commander.excessArguments',
      "error: too many arguments for 'speak'. Expected 0 arguments but got 1.",
    );
  });

  test('when specify --help then throw CommanderError', () => {
    const writeSpy = jest
      .spyOn(process.stdout, 'write')
      .mockImplementation(() => {});
    const program = new commander.Command();
    program.exitOverride();

    let caughtErr;
    try {
      program.parse(['node', 'test', '--help']);
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      0,
      'commander.helpDisplayed',
      '(outputHelp)',
    );
    writeSpy.mockRestore();
  });

  test('when executable subcommand and no command specified then throw CommanderError', () => {
    const program = new commander.Command();
    program.exitOverride().command('compress', 'compress description');

    let caughtErr;
    try {
      program.parse(['node', 'test']);
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(caughtErr, 1, 'commander.help', '(outputHelp)');
  });

  test('when specify --version then throw CommanderError', () => {
    const stdoutSpy = jest
      .spyOn(process.stdout, 'write')
      .mockImplementation(() => {});
    const myVersion = '1.2.3';
    const program = new commander.Command();
    program.exitOverride().version(myVersion);

    let caughtErr;
    try {
      program.parse(['node', 'test', '--version']);
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(caughtErr, 0, 'commander.version', myVersion);
    stdoutSpy.mockRestore();
  });

  test('when executableSubcommand succeeds then call exitOverride', async () => {
    expect.hasAssertions();
    const pm = path.join(__dirname, 'fixtures/pm');
    const program = new commander.Command();
    await new Promise((resolve) => {
      program
        .exitOverride((err) => {
          expectCommanderError(
            err,
            0,
            'commander.executeSubCommandAsync',
            '(close)',
          );
          resolve();
        })
        .command('silent', 'description');
      program.parse(['node', pm, 'silent']);
    });
  });

  test('when mandatory program option missing then throw CommanderError', () => {
    const optionFlags = '-p, --pepper <type>';
    const program = new commander.Command();
    program.exitOverride().requiredOption(optionFlags, 'add pepper');

    let caughtErr;
    try {
      program.parse(['node', 'test']);
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      1,
      'commander.missingMandatoryOptionValue',
      `error: required option '${optionFlags}' not specified`,
    );
  });

  test('when option argument not in choices then throw CommanderError', () => {
    const optionFlags = '--colour <shade>';
    const program = new commander.Command();
    program
      .exitOverride()
      .addOption(new commander.Option(optionFlags).choices(['red', 'blue']));

    let caughtErr;
    try {
      program.parse(['--colour', 'green'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      1,
      'commander.invalidArgument',
      "error: option '--colour <shade>' argument 'green' is invalid. Allowed choices are red, blue.",
    );
  });

  test('when command argument not in choices then throw CommanderError', () => {
    const program = new commander.Command();
    program
      .exitOverride()
      .addArgument(new commander.Argument('<shade>').choices(['red', 'blue']))
      .action(() => {});

    let caughtErr;
    try {
      program.parse(['green'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      1,
      'commander.invalidArgument',
      "error: command-argument value 'green' is invalid for argument 'shade'. Allowed choices are red, blue.",
    );
  });

  test('when custom processing for option throws InvalidArgumentError then catch CommanderError', () => {
    function justSayNo(value) {
      throw new commander.InvalidArgumentError('NO');
    }
    const optionFlags = '--colour <shade>';
    const program = new commander.Command();
    program.exitOverride().option(optionFlags, 'specify shade', justSayNo);

    let caughtErr;
    try {
      program.parse(['--colour', 'green'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      1,
      'commander.invalidArgument',
      "error: option '--colour <shade>' argument 'green' is invalid. NO",
    );
  });

  test('when custom processing for argument throws InvalidArgumentError then catch CommanderError', () => {
    function justSayNo(value) {
      throw new commander.InvalidArgumentError('NO');
    }
    const program = new commander.Command();
    program
      .exitOverride()
      .argument('[n]', 'number', justSayNo)
      .action(() => {});

    let caughtErr;
    try {
      program.parse(['green'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      1,
      'commander.invalidArgument',
      "error: command-argument value 'green' is invalid for argument 'n'. NO",
    );
  });

  test('when has conflicting option then throw CommanderError', () => {
    const program = new commander.Command();
    program
      .exitOverride()
      .addOption(new commander.Option('--silent'))
      .addOption(new commander.Option('--debug').conflicts(['silent']));

    let caughtErr;
    try {
      program.parse(['--debug', '--silent'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(
      caughtErr,
      1,
      'commander.conflictingOption',
      "error: option '--debug' cannot be used with option '--silent'",
    );
  });

  test('when call error() then throw CommanderError', () => {
    const program = new commander.Command();
    program.exitOverride();

    let caughtErr;
    try {
      program.error('message');
    } catch (err) {
      caughtErr = err;
    }

    expectCommanderError(caughtErr, 1, 'commander.error', 'message');
  });
});

test('when no override and error then exit(1)', () => {
  const exitSpy = jest.spyOn(process, 'exit').mockImplementation(() => {});
  const program = new commander.Command();
  program.configureOutput({ outputError: () => {} });
  program.parse(['--unknownOption'], { from: 'user' });
  expect(exitSpy).toHaveBeenCalledWith(1);
  exitSpy.mockRestore();
});

test('when custom processing throws custom error then throw custom error', () => {
  function justSayNo(value) {
    throw new Error('custom');
  }
  const program = new commander.Command();
  program
    .exitOverride()
    .option('-s, --shade <value>', 'specify shade', justSayNo);

  expect(() => {
    program.parse(['--shade', 'green'], { from: 'user' });
  }).toThrow('custom');
});

tests/command.executableSubcommand.searc.source:
const childProcess = require('child_process');
const fs = require('fs');
const path = require('path');
const commander = require('../');

// This file does in-process mocking. Bit clumsy but faster and less external clutter than using fixtures.
// See also command.executableSubcommand.lookup.test.js for tests using fixtures.

const gLocalDirectory = path.resolve(__dirname, 'fixtures'); // Real directory, although not actually searching for files in it.

function extractMockSpawnArgs(mock) {
  expect(mock).toHaveBeenCalled();
  // non-Win, launchWithNode: childProcess.spawn(process.argv[0], args, { stdio: 'inherit' });
  // Win always: childProcess.spawn(process.execPath, args, { stdio: 'inherit' });
  return mock.mock.calls[0][1];
}

function extractMockSpawnCommand(mock) {
  expect(mock).toHaveBeenCalled();
  // child_process.spawn(command[, args][, options])
  return mock.mock.calls[0][0];
}

const describeOrSkipOnWindows =
  process.platform === 'win32' ? describe.skip : describe;

describe('search for subcommand', () => {
  let spawnSpy;
  let existsSpy;

  beforeAll(() => {
    spawnSpy = jest.spyOn(childProcess, 'spawn').mockImplementation(() => {
      return {
        on: () => {},
        killed: true,
      };
    });
  });

  beforeEach(() => {
    existsSpy = jest.spyOn(fs, 'existsSync');
  });

  afterEach(() => {
    spawnSpy.mockClear();
    existsSpy.mockRestore();
  });

  afterAll(() => {
    spawnSpy.mockRestore();
  });

  // fs.existsSync gets called on Windows outside the search, so skip the tests (or come up with a different way of checking).
  describe('whether perform search for local files', () => {
    beforeEach(() => {
      existsSpy.mockImplementation(() => false);
    });

    test('when no script arg or executableDir then no search for local file', () => {
      const program = new commander.Command();
      program._checkForMissingExecutable = () => {}; // suppress error, call mocked spawn
      program.name('pm');
      program.command('sub', 'executable description');
      program.parse(['sub'], { from: 'user' });

      expect(existsSpy).not.toHaveBeenCalled();
    });

    test('when script arg then search for local files', () => {
      const program = new commander.Command();
      program._checkForMissingExecutable = () => {}; // suppress error, call mocked spawn
      program.name('pm');
      program.command('sub', 'executable description');
      program.parse(['node', 'script-name', 'sub']);

      expect(existsSpy).toHaveBeenCalled();
    });

    test('when executableDir then search for local files)', () => {
      const program = new commander.Command();
      program._checkForMissingExecutable = () => {}; // suppress error, call mocked spawn
      program.name('pm');
      program.executableDir(__dirname);
      program.command('sub', 'executable description');
      program.parse(['sub'], { from: 'user' });

      expect(existsSpy).toHaveBeenCalled();
    });
  });

  // We always use node on Windows, and don't spawn executable as the command (which may be a feature or a shortcoming!?).
  describeOrSkipOnWindows(
    'subcommand command name with no matching local file (non-Windows)',
    () => {
      beforeEach(() => {
        existsSpy.mockImplementation(() => false);
      });

      test('when named pm and no script arg or executableDir then spawn pm-sub as command', () => {
        const program = new commander.Command();
        program.name('pm');
        program.command('sub', 'executable description');
        program.parse(['sub'], { from: 'user' });

        expect(extractMockSpawnCommand(spawnSpy)).toEqual('pm-sub');
      });

      test('when named pm and script arg then still spawn pm-sub as command', () => {
        const program = new commander.Command();
        program.name('pm');
        program.command('sub', 'executable description');
        program.parse(['node', 'script-name', 'sub']);

        expect(extractMockSpawnCommand(spawnSpy)).toEqual('pm-sub');
      });

      test('when no name and script arg then spawn script-sub as command', () => {
        const program = new commander.Command();
        program.command('sub', 'executable description');
        program.parse(['node', 'script.js', 'sub']);

        expect(extractMockSpawnCommand(spawnSpy)).toEqual('script-sub');
      });

      test('when named pm and script arg and executableFile then spawn executableFile as command', () => {
        const program = new commander.Command();
        program.command('sub', 'executable description', {
          executableFile: 'myExecutable',
        });
        program.parse(['node', 'script.js', 'sub']);

        expect(extractMockSpawnCommand(spawnSpy)).toEqual('myExecutable');
      });
    },
  );

  describe('subcommand command name with matching local file', () => {
    test('when construct with name pm and script arg then spawn local pm-sub.js', () => {
      const program = new commander.Command('pm');
      program.command('sub', 'executable description');

      const localPath = path.resolve(gLocalDirectory, 'pm-sub.js');
      existsSpy.mockImplementation((path) => path === localPath);
      program.parse([
        'node',
        path.resolve(gLocalDirectory, 'script.js'),
        'sub',
      ]);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });

    test('when name pm and script arg then spawn local pm-sub.js', () => {
      const program = new commander.Command();
      program.name('pm');
      program.command('sub', 'executable description');

      const localPath = path.resolve(gLocalDirectory, 'pm-sub.js');
      existsSpy.mockImplementation((path) => path === localPath);
      program.parse([
        'node',
        path.resolve(gLocalDirectory, 'script.js'),
        'sub',
      ]);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });

    test('when script arg then spawn local script-sub.js', () => {
      const program = new commander.Command();
      program.command('sub', 'executable description');

      const localPath = path.resolve(gLocalDirectory, 'script-sub.js');
      existsSpy.mockImplementation((path) => path === localPath);
      program.parse([
        'node',
        path.resolve(gLocalDirectory, 'script.js'),
        'sub',
      ]);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });

    test('when name pm and script arg and only script-sub.js then fallback to spawn local script-sub.js', () => {
      const program = new commander.Command();
      program.name('pm');
      program.command('sub', 'executable description');

      // Fallback for compatibility with Commander <= v8
      const localPath = path.resolve(gLocalDirectory, 'script-sub.js');
      existsSpy.mockImplementation((path) => path === localPath);
      program.parse([
        'node',
        path.resolve(gLocalDirectory, 'script.js'),
        'sub',
      ]);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });

    test('when name pm and executableDir then spawn local pm-sub.js', () => {
      const program = new commander.Command();
      program.name('pm');
      program.command('sub', 'executable description');

      const execDir = path.resolve(gLocalDirectory, 'exec-dir');
      program.executableDir(execDir);
      const localPath = path.resolve(execDir, 'pm-sub.js');
      existsSpy.mockImplementation((path) => path === localPath);
      program.parse(['sub'], { from: 'user' });

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });

    test('when script arg and relative executableDir then spawn relative script-sub.js', () => {
      const program = new commander.Command();
      program.command('sub', 'executable description');

      const execDir = 'exec-dir';
      program.executableDir(execDir);
      const localPath = path.resolve(gLocalDirectory, execDir, 'script-sub.js');
      existsSpy.mockImplementation((path) => path === localPath);
      program.parse(['node', path.resolve(gLocalDirectory, 'script'), 'sub']);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });

    test('when script arg and absolute executableDir then spawn absolute script-sub.js', () => {
      const program = new commander.Command();
      program.command('sub', 'executable description');

      const execDir = path.resolve(gLocalDirectory, 'exec-dir');
      program.executableDir(execDir);
      const localPath = path.resolve(execDir, 'script-sub.js');
      existsSpy.mockImplementation((path) => path === localPath);
      program.parse([
        'node',
        path.resolve(gLocalDirectory, 'script-Dir', 'script'),
        'sub',
      ]);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });

    test('when script arg is link and and link-sub relative to link target then spawn local link-sub', () => {
      const program = new commander.Command();
      program.command('sub', 'executable description');

      const linkPath = path.resolve(gLocalDirectory, 'link', 'link');
      const scriptPath = path.resolve(gLocalDirectory, 'script', 'script.js');
      const scriptSubPath = path.resolve(
        gLocalDirectory,
        'script',
        'link-sub.js',
      );
      const realPathSyncSpy = jest
        .spyOn(fs, 'realpathSync')
        .mockImplementation((path) => {
          return path === linkPath ? scriptPath : linkPath;
        });
      existsSpy.mockImplementation((path) => path === scriptSubPath);
      program.parse(['node', linkPath, 'sub']);
      realPathSyncSpy.mockRestore();

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([scriptSubPath]);
    });

    test('when name pm and script arg and relative executableFile then spawn local exec.js', () => {
      const program = new commander.Command('pm');
      const localPath = path.join('relative', 'exec.js');
      const absolutePath = path.resolve(gLocalDirectory, localPath);
      program.command('sub', 'executable description', {
        executableFile: localPath,
      });

      existsSpy.mockImplementation((path) => path === absolutePath);
      program.parse([
        'node',
        path.resolve(gLocalDirectory, 'script.js'),
        'sub',
      ]);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([absolutePath]);
    });

    test('when name pm and script arg and absolute executableFile then spawn local exec.js', () => {
      const program = new commander.Command('pm');
      const localPath = path.resolve(gLocalDirectory, 'absolute', 'exec.js');
      program.command('sub', 'executable description', {
        executableFile: localPath,
      });

      existsSpy.mockImplementation((path) => path === localPath);
      program.parse([
        'node',
        path.resolve(gLocalDirectory, 'script.js'),
        'sub',
      ]);

      expect(extractMockSpawnArgs(spawnSpy)).toEqual([localPath]);
    });
  });

  describe('search for local file', () => {
    test('when script arg then search for local script-sub.js, .ts, .tsx, .mpjs, .cjs', () => {
      existsSpy.mockImplementation((path) => false);
      const program = new commander.Command();
      program._checkForMissingExecutable = () => {}; // suppress error, call mocked spawn
      program.command('sub', 'executable description');
      const scriptPath = path.resolve(gLocalDirectory, 'script');
      program.parse(['node', scriptPath, 'sub']);
      const sourceExt = ['.js', '.ts', '.tsx', '.mjs', '.cjs'];
      sourceExt.forEach((ext) => {
        expect(existsSpy).toHaveBeenCalledWith(
          path.resolve(gLocalDirectory, `script-sub${ext}`),
        );
      });
    });
  });
});

tests/options.conflicts.test.js.source:
const path = require('path');
const commander = require('../');

describe('command with conflicting options', () => {
  function makeProgram() {
    const actionMock = jest.fn();
    const program = new commander.Command();
    program
      .exitOverride()
      .configureOutput({
        writeErr: () => {},
        writeOut: () => {},
      })
      .command('foo')
      .addOption(
        new commander.Option('-s, --silent', "Don't print anything").env(
          'SILENT',
        ),
      )
      .addOption(
        new commander.Option('-j, --json', 'Format output as json')
          .env('JSON')
          .conflicts(['silent']),
      )
      .action(actionMock);

    return { program, actionMock };
  }

  beforeEach(() => {
    delete process.env.SILENT;
    delete process.env.JSON;
    delete process.env.DUAL;
    delete process.env.NO_DUAL;
  });

  test('should call action if there are no explicit conflicting options set', () => {
    const { program, actionMock } = makeProgram();
    program.parse('node test.js foo --json'.split(' '));
    expect(actionMock).toHaveBeenCalledTimes(1);
    expect(actionMock).toHaveBeenCalledWith({ json: true }, expect.any(Object));
  });

  test('should call action when there are no implicit conflicting options set', () => {
    const { program, actionMock } = makeProgram();
    program.parse('node test.js foo --silent'.split(' '));
    expect(actionMock).toHaveBeenCalledTimes(1);
    expect(actionMock).toHaveBeenCalledWith(
      { silent: true },
      expect.any(Object),
    );
  });

  test('should exit with error if conflicting options were set', () => {
    const { program } = makeProgram();

    expect(() => {
      program.parse('node test.js foo --silent --json'.split(' '));
    }).toThrow(
      "error: option '-j, --json' cannot be used with option '-s, --silent'",
    );
  });

  test('should report the env variable as the conflicting option source, when conflicting option is set', () => {
    const { program } = makeProgram();

    process.env.SILENT = true;

    expect(() => {
      program.parse('node test.js foo --json'.split(' '));
    }).toThrow(
      "error: option '-j, --json' cannot be used with environment variable 'SILENT'",
    );
  });

  test('should report the env variable as the configured option source, when configured option is set', () => {
    const { program } = makeProgram();

    process.env.JSON = true;

    expect(() => {
      program.parse('node test.js foo --silent'.split(' '));
    }).toThrow(
      "error: environment variable 'JSON' cannot be used with option '-s, --silent'",
    );
  });

  test('should report both env variables as sources, when configured option and conflicting option are set', () => {
    const { program } = makeProgram();

    process.env.SILENT = true;
    process.env.JSON = true;

    expect(() => {
      program.parse('node test.js foo'.split(' '));
    }).toThrow(
      "error: environment variable 'JSON' cannot be used with environment variable 'SILENT'",
    );
  });

  test('should allow default value with a conflicting option', () => {
    const { program, actionMock } = makeProgram();

    program.commands[0].addOption(
      new commander.Option('-d, --debug', 'print debug logs')
        .default(true)
        .conflicts(['silent']),
    );

    program.parse('node test.js foo --silent'.split(' '));

    expect(actionMock).toHaveBeenCalledTimes(1);
    expect(actionMock).toHaveBeenCalledWith(
      { debug: true, silent: true },
      expect.any(Object),
    );
  });

  test('should report conflict on negated option flag', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red').conflicts(['color']))
      .addOption(new commander.Option('--color'))
      .addOption(new commander.Option('-N, --no-color'));

    expect(() => {
      program.parse('node test.js bar --red -N'.split(' '));
    }).toThrow(
      "error: option '--red' cannot be used with option '-N, --no-color'",
    );
  });

  test('should report conflict on negated option env variable', () => {
    const { program } = makeProgram();

    process.env.NO_COLOR = true;

    program
      .command('bar')
      .addOption(new commander.Option('--red').conflicts(['color']))
      .addOption(new commander.Option('--color'))
      .addOption(new commander.Option('-N, --no-color').env('NO_COLOR'));

    expect(() => {
      program.parse('node test.js bar --red'.split(' '));
    }).toThrow(
      "error: option '--red' cannot be used with environment variable 'NO_COLOR'",
    );
  });

  test('should report correct error for shorthand negated option', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red'))
      .addOption(new commander.Option('-N, --no-color').conflicts(['red']));

    expect(() => {
      program.parse('node test.js bar --red -N'.split(' '));
    }).toThrow(
      "error: option '-N, --no-color' cannot be used with option '--red'",
    );
  });

  test('should report correct error for positive option when negated is configured', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red'))
      .addOption(new commander.Option('--dual').env('DUAL').conflicts(['red']))
      .addOption(new commander.Option('--no-dual').env('NO_DUAL'));

    expect(() => {
      program.parse('node test.js bar --red --dual'.split(' '));
    }).toThrow("error: option '--dual' cannot be used with option '--red'");
  });

  test('should report correct error for negated option when positive is configured', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red'))
      .addOption(new commander.Option('--dual').env('DUAL').conflicts(['red']))
      .addOption(new commander.Option('--no-dual').env('NO_DUAL'));

    expect(() => {
      program.parse('node test.js bar --red --no-dual'.split(' '));
    }).toThrow("error: option '--no-dual' cannot be used with option '--red'");
  });

  test('should report correct error for positive env variable when negated is configured', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red'))
      .addOption(new commander.Option('--dual').env('DUAL').conflicts(['red']))
      .addOption(new commander.Option('--no-dual').env('NO_DUAL'));

    process.env.DUAL = 'true';
    expect(() => {
      program.parse('node test.js bar --red'.split(' '));
    }).toThrow(
      "error: environment variable 'DUAL' cannot be used with option '--red'",
    );
  });

  test('should report correct error for negated env variable when positive is configured', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red'))
      .addOption(new commander.Option('--dual').env('DUAL').conflicts(['red']))
      .addOption(new commander.Option('--no-dual').env('NO_DUAL'));

    process.env.NO_DUAL = 'true';
    expect(() => {
      program.parse('node test.js bar --red'.split(' '));
    }).toThrow(
      "error: environment variable 'NO_DUAL' cannot be used with option '--red'",
    );
  });

  test('should report correct error for positive option with string value when negated is configured', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red'))
      .addOption(new commander.Option('--dual2 <str>').conflicts(['red']))
      .addOption(new commander.Option('--no-dual2').preset('BAD'));

    expect(() => {
      program.parse('node test.js bar --red --dual2 foo'.split(' '));
    }).toThrow(
      "error: option '--dual2 <str>' cannot be used with option '--red'",
    );
  });

  test('should report correct error for negated option with preset when negated is configured', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--red'))
      .addOption(new commander.Option('--dual2 <str>').conflicts(['red']))
      .addOption(new commander.Option('--no-dual2').preset('BAD'));

    expect(() => {
      program.parse('node test.js bar --red --no-dual2'.split(' '));
    }).toThrow("error: option '--no-dual2' cannot be used with option '--red'");
  });

  test('should not throw error when conflicts is invoked with a single string that includes another option', () => {
    const { program } = makeProgram();

    const actionMock = jest.fn();

    program
      .command('bar')
      .addOption(new commander.Option('--a'))
      .addOption(new commander.Option('--b').conflicts('aa'))
      .action(actionMock);

    program.parse('node test.js bar --a --b'.split(' '));

    expect(actionMock).toHaveBeenCalledTimes(1);
    expect(actionMock).toHaveBeenCalledWith(
      { a: true, b: true },
      expect.any(Object),
    );
  });

  test('should throw error when conflicts is invoked with a single string that equals another option', () => {
    const { program } = makeProgram();

    program
      .command('bar')
      .addOption(new commander.Option('--a'))
      .addOption(new commander.Option('--b').conflicts('a'));

    expect(() => {
      program.parse('node test.js bar --a --b'.split(' '));
    }).toThrow("error: option '--b' cannot be used with option '--a'");
  });

  test('when conflict on program calling action subcommand then throw conflict', () => {
    const { program } = makeProgram();
    let exception;

    program
      .addOption(new commander.Option('--black'))
      .addOption(new commander.Option('--white').conflicts('black'));

    try {
      program.parse('--white --black foo'.split(' '), { from: 'user' });
    } catch (err) {
      exception = err;
    }
    expect(exception).not.toBeUndefined();
    expect(exception.code).toBe('commander.conflictingOption');
  });

  test('when conflict on program calling action subcommand with help then show help', () => {
    const { program } = makeProgram();
    let exception;

    program
      .addOption(new commander.Option('--black'))
      .addOption(new commander.Option('--white').conflicts('black'));

    try {
      program.parse('--white --black foo --help'.split(' '), { from: 'user' });
    } catch (err) {
      exception = err;
    }
    expect(exception).not.toBeUndefined();
    expect(exception.code).toBe('commander.helpDisplayed');
  });

  test('when conflict on program calling external subcommand then throw conflict', () => {
    const { program } = makeProgram();
    let exception;

    program
      .addOption(new commander.Option('--black'))
      .addOption(new commander.Option('--white').conflicts('black'));
    const pm = path.join(__dirname, './fixtures/pm');
    program.command('ext', 'external command', { executableFile: pm });

    try {
      program.parse('--white --black ext'.split(' '), { from: 'user' });
    } catch (err) {
      exception = err;
    }
    expect(exception).not.toBeUndefined();
    expect(exception.code).toBe('commander.conflictingOption');
  });
});

tests/command.help.test.js.source:
const commander = require('../');

// Testing various help incantations.
//
// Note there are also specific help tests included in many of the feature tests,
// such as the alias, version, usage, name, helpOption, and commandHelp tests.

// Avoid doing many full format tests as will be broken by any layout changes!
test('when call helpInformation for program then help format is as expected (usage, options, commands)', () => {
  const program = new commander.Command();
  program.command('my-command <file>');
  const expectedHelpInformation = `Usage: test [options] [command]

Options:
  -h, --help         display help for command

Commands:
  my-command <file>
  help [command]     display help for command
`;

  program.name('test');
  const helpInformation = program.helpInformation();
  expect(helpInformation).toBe(expectedHelpInformation);
});

test('when use .description for command then help includes description', () => {
  const program = new commander.Command();
  program.command('simple-command').description('custom-description');
  program._help = 'test';
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch(/simple-command +custom-description/);
});

test('when call .help then exit', () => {
  // Optional. Suppress normal output to keep test output clean.
  const writeSpy = jest
    .spyOn(process.stdout, 'write')
    .mockImplementation(() => {});
  const program = new commander.Command();
  program.exitOverride();
  expect(() => {
    program.help();
  }).toThrow('(outputHelp)');
  writeSpy.mockClear();
});

test('when specify --help then exit', () => {
  // Optional. Suppress normal output to keep test output clean.
  const writeSpy = jest
    .spyOn(process.stdout, 'write')
    .mockImplementation(() => {});
  const program = new commander.Command();
  program.exitOverride();
  expect(() => {
    program.parse(['node', 'test', '--help']);
  }).toThrow('(outputHelp)');
  writeSpy.mockClear();
});

test('when call help(cb) then display cb output and exit', () => {
  // Using spy to detect custom output
  const writeSpy = jest
    .spyOn(process.stdout, 'write')
    .mockImplementation(() => {});
  const helpReplacement = 'reformatted help';
  const program = new commander.Command();
  program.exitOverride();
  expect(() => {
    program.help((helpInformation) => {
      return helpReplacement;
    });
  }).toThrow('(outputHelp)');
  expect(writeSpy).toHaveBeenCalledWith(helpReplacement);
  writeSpy.mockClear();
});

test('when call outputHelp(cb) then display cb output', () => {
  // Using spy to detect custom output
  const writeSpy = jest
    .spyOn(process.stdout, 'write')
    .mockImplementation(() => {});
  const helpReplacement = 'reformatted help';
  const program = new commander.Command();
  program.outputHelp((helpInformation) => {
    return helpReplacement;
  });
  expect(writeSpy).toHaveBeenCalledWith(helpReplacement);
  writeSpy.mockClear();
});

test('when call deprecated outputHelp(cb) with wrong callback return type then throw', () => {
  const program = new commander.Command();
  expect(() => {
    program.outputHelp((helpInformation) => 3);
  }).toThrow();
});

test('when command sets deprecated noHelp then not displayed in helpInformation', () => {
  const program = new commander.Command();
  program.command('secret', 'secret description', { noHelp: true });
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch('secret');
});

test('when command sets hidden then not displayed in helpInformation', () => {
  const program = new commander.Command();
  program.command('secret', 'secret description', { hidden: true });
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch('secret');
});

test('when addCommand with hidden:true then not displayed in helpInformation', () => {
  const secretCmd = new commander.Command('secret');

  const program = new commander.Command();
  program.addCommand(secretCmd, { hidden: true });
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch('secret');
});

test('when help short flag masked then not displayed in helpInformation', () => {
  const program = new commander.Command();
  program.option('-h, --host', 'select host');
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch(/\W-h\W.*display help/);
});

test('when both help flags masked then not displayed in helpInformation', () => {
  const program = new commander.Command();
  program.option('-h, --help', 'custom');
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch('display help');
});

test('when call .help then output on stdout', () => {
  const writeSpy = jest
    .spyOn(process.stdout, 'write')
    .mockImplementation(() => {});
  const program = new commander.Command();
  program.exitOverride();
  expect(() => {
    program.help();
  }).toThrow('(outputHelp)');
  expect(writeSpy).toHaveBeenCalledWith(program.helpInformation());
  writeSpy.mockClear();
});

test('when call .help with { error: true } then output on stderr', () => {
  const writeSpy = jest
    .spyOn(process.stderr, 'write')
    .mockImplementation(() => {});
  const program = new commander.Command();
  program.exitOverride();
  expect(() => {
    program.help({ error: true });
  }).toThrow('(outputHelp)');
  expect(writeSpy).toHaveBeenCalledWith(program.helpInformation());
  writeSpy.mockClear();
});

test('when no options then Options not included in helpInformation', () => {
  const program = new commander.Command();
  // No custom options, no version option, no help option
  program.helpOption(false);
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch('Options');
});

test('when negated option then option included in helpInformation', () => {
  const program = new commander.Command();
  program.option('-C, --no-colour', 'colourless');
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('--no-colour');
  expect(helpInformation).toMatch('colourless');
});

test('when option.hideHelp() then option not included in helpInformation', () => {
  const program = new commander.Command();
  program.addOption(
    new commander.Option('-s,--secret', 'secret option').hideHelp(),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch('secret');
});

test('when option.hideHelp(true) then option not included in helpInformation', () => {
  const program = new commander.Command();
  program.addOption(
    new commander.Option('-s,--secret', 'secret option').hideHelp(true),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).not.toMatch('secret');
});

test('when option.hideHelp(false) then option included in helpInformation', () => {
  const program = new commander.Command();
  program.addOption(
    new commander.Option('-s,--secret', 'secret option').hideHelp(false),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('secret');
});

test('when option has default value then default included in helpInformation', () => {
  const program = new commander.Command();
  program.option('-p, --port <portNumber>', 'port number', 80);
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('(default: 80)');
});

test('when option has default value description then default description included in helpInformation', () => {
  const program = new commander.Command();
  program.addOption(
    new commander.Option('-a, --address <dotted>', 'ip address').default(
      '127.0.0.1',
      'home',
    ),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('(default: home)');
});

test('when option has choices then choices included in helpInformation', () => {
  const program = new commander.Command();
  program.addOption(
    new commander.Option('-c, --colour <colour>').choices(['red', 'blue']),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('(choices: "red", "blue")');
});

test('when option has choices and default then both included in helpInformation', () => {
  const program = new commander.Command();
  program.addOption(
    new commander.Option('-c, --colour <colour>')
      .choices(['red', 'blue'])
      .default('red'),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('(choices: "red", "blue", default: "red")');
});

test('when argument then included in helpInformation', () => {
  const program = new commander.Command();
  program.name('foo').argument('<file>');
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('Usage: foo [options] <file>');
});

test('when argument described then included in helpInformation', () => {
  const program = new commander.Command();
  program.argument('<file>', 'input source').helpOption(false);
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch(/Arguments:\n +file +input source/);
});

test('when argument described with default then included in helpInformation', () => {
  const program = new commander.Command();
  program.argument('[file]', 'input source', 'test.txt').helpOption(false);
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch(
    /Arguments:\n +file +input source \(default: "test.txt"\)/,
  );
});

test('when arguments described in deprecated way then included in helpInformation', () => {
  const program = new commander.Command();
  program
    .arguments('<file>')
    .helpOption(false)
    .description('description', { file: 'input source' });
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch(/Arguments:\n +file +input source/);
});

test('when arguments described in deprecated way and empty description then arguments still included in helpInformation', () => {
  const program = new commander.Command();
  program
    .arguments('<file>')
    .helpOption(false)
    .description('', { file: 'input source' });
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch(/Arguments:\n +file +input source/);
});

test('when argument has choices then choices included in helpInformation', () => {
  const program = new commander.Command();
  program.addArgument(
    new commander.Argument('<colour>', 'preferred colour').choices([
      'red',
      'blue',
    ]),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('(choices: "red", "blue")');
});

test('when argument has choices and default then both included in helpInformation', () => {
  const program = new commander.Command();
  program.addArgument(
    new commander.Argument('<colour>', 'preferred colour')
      .choices(['red', 'blue'])
      .default('red'),
  );
  const helpInformation = program.helpInformation();
  expect(helpInformation).toMatch('(choices: "red", "blue", default: "red")');
});

tests/helpGroup.test.js.source:
const { Command, Option } = require('../');

// Similar tests for Option.helpGroup() and Command.helpGroup(),
// and for Command.optionsGroup() and Command.commandsGroup().

describe('Option.helpGroup', () => {
  test('when add one option with helpGroup then help contains group', () => {
    const program = new Command();
    program.addOption(new Option('--alpha').helpGroup('Greek:'));
    const helpInfo = program.helpInformation();
    expect(helpInfo).toMatch(/Greek:\n *--alpha/);
  });

  test('when add two options with helpGroup then help contains group', () => {
    const program = new Command();
    program.addOption(new Option('--alpha').helpGroup('Greek:'));
    program.addOption(new Option('--beta').helpGroup('Greek:'));
