tests/command.addHelpText.test.js.source:
const commander = require('../');

// Using outputHelp to simplify testing.

describe('program calls to addHelpText', () => {
  let writeSpy;

  beforeAll(() => {
    writeSpy = jest.spyOn(process.stdout, 'write').mockImplementation(() => {});
  });

  afterEach(() => {
    writeSpy.mockClear();
  });

  afterAll(() => {
    writeSpy.mockRestore();
  });

  test('when "before" string then string before built-in help', () => {
    const program = new commander.Command();
    program.addHelpText('before', 'text');
    program.outputHelp();
    expect(writeSpy).toHaveBeenNthCalledWith(1, 'text\n');
    expect(writeSpy).toHaveBeenNthCalledWith(2, program.helpInformation());
  });

  test('when "before" function then function result before built-in help', () => {
    const program = new commander.Command();
    program.addHelpText('before', () => 'text');
    program.outputHelp();
    expect(writeSpy).toHaveBeenNthCalledWith(1, 'text\n');
    expect(writeSpy).toHaveBeenNthCalledWith(2, program.helpInformation());
  });

  test('when "before" function returns nothing then no effect', () => {
    const program = new commander.Command();
    program.addHelpText('before', () => {});
    program.outputHelp();
    expect(writeSpy).toHaveBeenNthCalledWith(1, program.helpInformation());
  });

  test('when "beforeAll" string then string before built-in help', () => {
    const program = new commander.Command();
    program.addHelpText('beforeAll', 'text');
    program.outputHelp();
    expect(writeSpy).toHaveBeenNthCalledWith(1, 'text\n');
    expect(writeSpy).toHaveBeenNthCalledWith(2, program.helpInformation());
  });

  test('when "after" string then string after built-in help', () => {
    const program = new commander.Command();
    program.addHelpText('after', 'text');
    program.outputHelp();
    expect(writeSpy).toHaveBeenNthCalledWith(1, program.helpInformation());
    expect(writeSpy).toHaveBeenNthCalledWith(2, 'text\n');
  });

  test('when "afterAll" string then string after built-in help', () => {
    const program = new commander.Command();
    program.addHelpText('afterAll', 'text');
    program.outputHelp();
    expect(writeSpy).toHaveBeenNthCalledWith(1, program.helpInformation());
    expect(writeSpy).toHaveBeenNthCalledWith(2, 'text\n');
  });

  test('when all the simple positions then strings in order', () => {
    const program = new commander.Command();
    program.addHelpText('before', 'before');
    program.addHelpText('after', 'after');
    program.addHelpText('beforeAll', 'beforeAll');
    program.addHelpText('afterAll', 'afterAll');
    program.outputHelp();
    expect(writeSpy).toHaveBeenNthCalledWith(1, 'beforeAll\n');
    expect(writeSpy).toHaveBeenNthCalledWith(2, 'before\n');
    expect(writeSpy).toHaveBeenNthCalledWith(3, program.helpInformation());
    expect(writeSpy).toHaveBeenNthCalledWith(4, 'after\n');
    expect(writeSpy).toHaveBeenNthCalledWith(5, 'afterAll\n');
  });

  test('when "silly" position then throw', () => {
    const program = new commander.Command();
    expect(() => {
      program.addHelpText('silly', 'text');
    }).toThrow();
  });
});

describe('program and subcommand calls to addHelpText', () => {
  let writeSpy;

  beforeAll(() => {
    writeSpy = jest.spyOn(process.stdout, 'write').mockImplementation(() => {});
  });

  afterEach(() => {
    writeSpy.mockClear();
  });

  afterAll(() => {
    writeSpy.mockRestore();
  });

  test('when "before" on program then not called on subcommand', () => {
    const program = new commander.Command();
    const sub = program.command('sub');
    const testMock = jest.fn();
    program.addHelpText('before', testMock);
    sub.outputHelp();
    expect(testMock).not.toHaveBeenCalled();
  });

  test('when "beforeAll" on program then is called on subcommand', () => {
    const program = new commander.Command();
    const sub = program.command('sub');
    const testMock = jest.fn();
    program.addHelpText('beforeAll', testMock);
    sub.outputHelp();
    expect(testMock).toHaveBeenCalled();
  });

  test('when "after" on program then not called on subcommand', () => {
    const program = new commander.Command();
    const sub = program.command('sub');
    const testMock = jest.fn();
    program.addHelpText('after', testMock);
    sub.outputHelp();
    expect(testMock).not.toHaveBeenCalled();
  });

  test('when "afterAll" on program then is called on subcommand', () => {
    const program = new commander.Command();
    const sub = program.command('sub');
    const testMock = jest.fn();
    program.addHelpText('afterAll', testMock);
    sub.outputHelp();
    expect(testMock).toHaveBeenCalled();
  });
});

describe('context checks with full parse', () => {
  let stdoutSpy;
  let stderrSpy;

  beforeAll(() => {
    stdoutSpy = jest
      .spyOn(process.stdout, 'write')
      .mockImplementation(() => {});
    stderrSpy = jest
      .spyOn(process.stderr, 'write')
      .mockImplementation(() => {});
  });

  afterEach(() => {
    stdoutSpy.mockClear();
    stderrSpy.mockClear();
  });

  afterAll(() => {
    stdoutSpy.mockRestore();
    stderrSpy.mockRestore();
  });

  test('when help requested then text is on stdout', () => {
    const program = new commander.Command();
    program.exitOverride().addHelpText('before', 'text');
    expect(() => {
      program.parse(['--help'], { from: 'user' });
    }).toThrow();
    expect(stdoutSpy).toHaveBeenCalledWith('text\n');
  });

  test('when help for error then text is on stderr', () => {
    const program = new commander.Command();
    program.exitOverride().addHelpText('before', 'text').command('sub');
    expect(() => {
      program.parse([], { from: 'user' });
    }).toThrow();
    expect(stderrSpy).toHaveBeenCalledWith('text\n');
  });

  test('when help requested then context.error is false', () => {
    let context;
    const program = new commander.Command();
    program.exitOverride().addHelpText('before', (contextParam) => {
      context = contextParam;
    });
    expect(() => {
      program.parse(['--help'], { from: 'user' });
    }).toThrow();
    expect(context.error).toBe(false);
  });

  test('when help for error then context.error is true', () => {
    let context;
    const program = new commander.Command();
    program
      .exitOverride()
      .addHelpText('before', (contextParam) => {
        context = contextParam;
      })
      .command('sub');
    expect(() => {
      program.parse([], { from: 'user' });
    }).toThrow();
    expect(context.error).toBe(true);
  });

  test('when help on program then context.command is program', () => {
    let context;
    const program = new commander.Command();
    program.exitOverride().addHelpText('before', (contextParam) => {
      context = contextParam;
    });
    expect(() => {
      program.parse(['--help'], { from: 'user' });
    }).toThrow();
    expect(context.command).toBe(program);
  });

  test('when help on subcommand and "before" subcommand then context.command is subcommand', () => {
    let context;
    const program = new commander.Command();
    program.exitOverride();
    const sub = program.command('sub').addHelpText('before', (contextParam) => {
      context = contextParam;
    });
    expect(() => {
      program.parse(['sub', '--help'], { from: 'user' });
    }).toThrow();
    expect(context.command).toBe(sub);
  });

  test('when help on subcommand and "beforeAll" on program then context.command is subcommand', () => {
    let context;
    const program = new commander.Command();
    program.exitOverride().addHelpText('beforeAll', (contextParam) => {
      context = contextParam;
    });
    const sub = program.command('sub');
    expect(() => {
      program.parse(['sub', '--help'], { from: 'user' });
    }).toThrow();
    expect(context.command).toBe(sub);
  });
});

tests/negatives.test.js.source:
const { Command } = require('../');

// boolean is whether is a consumable argument when negative numbers allowed
const negativeNumbers = [
  ['-.1', true],
  ['-123', true],
  ['-123.45', true],
  ['-1e3', true],
  ['-1e+3', true],
  ['-1e-3', true],
  ['-1.2e3', true],
  ['-1.2e+3', true],
  ['-1.2e-3', true],
  ['-1e-3.0', false], // invalid number format
  ['--1 ', false], // invalid number format
  ['-0', true],
  ['1', true],
  ['-1x', false], // whole string is not a number
  ['-x-1 ', false], // whole string is not a number
  ['', true],
  ['-0x1234', false], // not a plain number
];

test.each(negativeNumbers)(
  `when option-argument for short optional is %s then consumed=%s`,
  (value, consume) => {
    const program = new Command();
    program.exitOverride().configureOutput({ writeErr: () => {} });
    program.option('-o, --optional [value]', 'optional option');
    const args = ['-o', value];
    let thrown = '';
    try {
      program.parse(args, { from: 'user' });
    } catch (err) {
      thrown = err.code;
    }

    expect(thrown).toEqual(consume ? '' : 'commander.unknownOption');
    // throws after setting optional to true
    expect(program.opts()['optional']).toBe(consume ? value : true);
  },
);

test.each(negativeNumbers)(
  `when option-argument for long optional is %s then consumed=%s`,
  (value, consume) => {
    const program = new Command();
    program.exitOverride().configureOutput({ writeErr: () => {} });
    program.option('-o, --optional [value]', 'optional option');
    const args = ['--optional', value];
    let thrown = '';
    try {
      program.parse(args, { from: 'user' });
    } catch (err) {
      thrown = err.code;
    }

    expect(thrown).toEqual(consume ? '' : 'commander.unknownOption');
    // throws after setting optional to true
    expect(program.opts()['optional']).toBe(consume ? value : true);
  },
);

test.each(negativeNumbers)(
  `when option-argument for short optional... is %s then consumed=%s`,
  (value, consume) => {
    const program = new Command();
    program
      .exitOverride()
      .configureOutput({ writeErr: () => {} })
      .option('-o, --optional [value...]', 'optional option');
    const args = ['-o', 'first', value];
    let thrown = '';
    try {
      program.parse(args, { from: 'user' });
    } catch (err) {
      thrown = err.code;
    }

    expect(thrown).toEqual(consume ? '' : 'commander.unknownOption');
    // throws after consuming 'first'
    expect(program.opts()['optional']).toEqual(
      consume ? ['first', value] : ['first'],
    );
  },
);

test.each(negativeNumbers)(
  `when option-argument for long optional... is %s then consumed=%s`,
  (value, consume) => {
    const program = new Command();
    program
      .exitOverride()
      .configureOutput({ writeErr: () => {} })
      .option('-o, --optional [value...]', 'optional option');
    const args = ['--optional', 'first', value];
    let thrown = '';
    try {
      program.parse(args, { from: 'user' });
    } catch (err) {
      thrown = err.code;
    }

    expect(thrown).toEqual(consume ? '' : 'commander.unknownOption');
    // throws after consuming 'first'
    expect(program.opts()['optional']).toEqual(
      consume ? ['first', value] : ['first'],
    );
  },
);

test.each(negativeNumbers)(
  `when command-argument is %s then consumed=%s`,
  (value, consume) => {
    const program = new Command();
    program
      .exitOverride()
      .configureOutput({ writeErr: () => {} })
      .argument('<value>', 'argument');
    const args = [value];
    let thrown = '';
    try {
      program.parse(args, { from: 'user' });
    } catch (err) {
      thrown = err.code;
    }

    expect(thrown).toEqual(consume ? '' : 'commander.unknownOption');
    expect(consume ? program.args : undefined).toEqual(
      consume ? [value] : undefined,
    );
  },
);

test.each(negativeNumbers)(
  `when digit option defined and option-argument is %s then negative not consumed`,
  (value, _ignore) => {
    const program = new Command();
    program
      .exitOverride()
      .configureOutput({ writeErr: () => {} })
      .option('-o, --optional [value]', 'optional option')
      .option('-9', 'register option using digit');
    const args = ['-o', value];
    let thrown = '';
    try {
      program.parse(args, { from: 'user' });
    } catch (err) {
      thrown = err.code;
    }

    let consume = value[0] !== '-';
    expect(thrown).toEqual(consume ? '' : 'commander.unknownOption');
    expect(program.opts()['optional']).toBe(consume ? value : true);
  },
);

test.each(negativeNumbers)(
  `when digit option defined and command-argument is %s then negative not consumed`,
  (value, _ignore) => {
    const program = new Command();
    program
      .exitOverride()
      .configureOutput({ writeErr: () => {} })
      .argument('[value]')
      .option('-9', 'register option using digit');
    const args = [value];
    let thrown = '';
    try {
      program.parse(args, { from: 'user' });
    } catch (err) {
      thrown = err.code;
    }

    let consume = value[0] !== '-';
    expect(thrown).toEqual(consume ? '' : 'commander.unknownOption');
    expect(consume ? program.args : undefined).toEqual(
      consume ? [value] : undefined,
    );
  },
);

test('when complex example with negative numbers then all consumed', () => {
  const program = new Command();
  program
    .option('-o [value]', 'optional')
    .option('-m <value>', 'required option-argument')
    .option('-O [value...]', 'optional')
    .option('-M <value...>', 'required option-argument')
    .argument('[value...]', 'argument');
  const args = [
    '-10',
    '-O',
    '-40',
    '-41',
    '-M',
    '-50',
    '-51',
    '-o',
    '-20',
    '-m',
    '-30',
    '-11',
  ];
  program.parse(args, { from: 'user' });
  expect(program.opts()).toEqual({
    o: '-20',
    m: '-30',
    O: ['-40', '-41'],
    M: ['-50', '-51'],
  });
  expect(program.args).toEqual(['-10', '-11']);
});

test('when program has digit option then negatives not allowed in leaf command', () => {
  const program = new Command();
  program
    .exitOverride()
    .configureOutput({ writeErr: () => {} })
    .option('-2', 'double option');
  let leafArgs;
  program
    .command('leaf')
    .argument('[value...]')
    .action((args) => {
      leafArgs = args;
    });
  const args = ['leaf', '-1'];
  expect(() => program.parse(args, { from: 'user' })).toThrow();
});

test('when default command without digit option then negatives accepted', () => {
  const program = new Command();
  let leafArgs;
  program
    .command('leaf', { isDefault: true })
    .argument('[value...]')
    .action((args) => {
      leafArgs = args;
    });
  program.parse(['-1'], { from: 'user' });
  expect(leafArgs).toEqual(['-1']);
});

test('when default command with digit option then negative throws', () => {
  const program = new Command();
  program.exitOverride().configureOutput({ writeErr: () => {} });
  program
    .command('leaf', { isDefault: true })
    .option('-2')
    .argument('[value...]')
    .action(() => {});
  expect(() => program.parse(['-1'], { from: 'user' })).toThrow();
});

test('when program has subcommand and action handler then negative command-argument unsupported', () => {
  // Known limitation in parsing. Only allowed negative command-arguments in leaf commands
  // to minimise changes to parsing when added support for negative numbers.
  const program = new Command();
  program
    .exitOverride()
    .configureOutput({ writeErr: () => {} })
    .argument('[value...]')
    .action(() => {});
  program.command('leaf').action(() => {});
  expect(() => program.parse(['-1'], { from: 'user' })).toThrow();
});

tests/argument.custom-processing.test.js.source:
const commander = require('../');

// Testing default value and custom processing behaviours.
// Some double assertions in tests to check action argument and .processedArg

test('when argument not specified then callback not called', () => {
  const mockCoercion = jest.fn();
  const program = new commander.Command();
  program.argument('[n]', 'number', mockCoercion).action(() => {});
  program.parse([], { from: 'user' });
  expect(mockCoercion).not.toHaveBeenCalled();
});

test('when argument not specified then action argument undefined', () => {
  let actionValue = 'foo';
  const program = new commander.Command();
  program.argument('[n]', 'number', parseFloat).action((arg) => {
    actionValue = arg;
  });
  program.parse([], { from: 'user' });
  expect(actionValue).toBeUndefined();
});

test('when custom with starting value and argument not specified then callback not called', () => {
  const mockCoercion = jest.fn();
  const program = new commander.Command();
  program.argument('[n]', 'number', parseFloat, 1).action(() => {});
  program.parse([], { from: 'user' });
  expect(mockCoercion).not.toHaveBeenCalled();
});

test('when custom with starting value and argument not specified with action handler then action argument is starting value', () => {
  const startingValue = 1;
  let actionValue;
  const program = new commander.Command();
  program.argument('[n]', 'number', parseFloat, startingValue).action((arg) => {
    actionValue = arg;
  });
  program.parse([], { from: 'user' });
  expect(actionValue).toEqual(startingValue);
  expect(program.processedArgs).toEqual([startingValue]);
});

test('when custom with starting value and argument not specified without action handler then .processedArgs has starting value', () => {
  const startingValue = 1;
  const program = new commander.Command();
  program.argument('[n]', 'number', parseFloat, startingValue);
  program.parse([], { from: 'user' });
  expect(program.processedArgs).toEqual([startingValue]);
});

test('when default value is defined (without custom processing) and argument not specified with action handler then action argument is default value', () => {
  const defaultValue = 1;
  let actionValue;
  const program = new commander.Command();
  program.argument('[n]', 'number', defaultValue).action((arg) => {
    actionValue = arg;
  });
  program.parse([], { from: 'user' });
  expect(actionValue).toEqual(defaultValue);
  expect(program.processedArgs).toEqual([defaultValue]);
});

test('when default value is defined (without custom processing) and argument not specified without action handler then .processedArgs is default value', () => {
  const defaultValue = 1;
  const program = new commander.Command();
  program.argument('[n]', 'number', defaultValue);
  program.parse([], { from: 'user' });
  expect(program.processedArgs).toEqual([defaultValue]);
});

test('when argument specified then callback called with value', () => {
  const mockCoercion = jest.fn();
  const value = '1';
  const program = new commander.Command();
  program.argument('[n]', 'number', mockCoercion).action(() => {});
  program.parse([value], { from: 'user' });
  expect(mockCoercion).toHaveBeenCalledWith(value, undefined);
});

test('when argument specified with action handler then action value is as returned from callback', () => {
  const callbackResult = 2;
  let actionValue;
  const program = new commander.Command();
  program
    .argument('[n]', 'number', () => {
      return callbackResult;
    })
    .action((arg) => {
      actionValue = arg;
    });
  program.parse(['node', 'test', 'alpha']);
  expect(actionValue).toEqual(callbackResult);
  expect(program.processedArgs).toEqual([callbackResult]);
});

test('when argument specified without action handler then .processedArgs is as returned from callback', () => {
  const callbackResult = 2;
  const program = new commander.Command();
  program.argument('[n]', 'number', () => {
    return callbackResult;
  });
  program.parse(['node', 'test', 'alpha']);
  expect(program.processedArgs).toEqual([callbackResult]);
});

test('when argument specified then program.args has original rather than custom', () => {
  // This is as intended, so check behaviour.
  const callbackResult = 2;
  const program = new commander.Command();
  program
    .argument('[n]', 'number', () => {
      return callbackResult;
    })
    .action(() => {});
  program.parse(['node', 'test', 'alpha']);
  expect(program.args).toEqual(['alpha']);
});

test('when custom with starting value and argument specified then callback called with value and starting value', () => {
  const mockCoercion = jest.fn();
  const startingValue = 1;
  const value = '2';
  const program = new commander.Command()
    .argument('[n]', 'number', mockCoercion, startingValue)
    .action(() => {});
  program.parse(['node', 'test', value]);
  expect(mockCoercion).toHaveBeenCalledWith(value, startingValue);
});

test('when variadic argument specified multiple times then callback called with value and previousValue', () => {
  const mockCoercion = jest.fn().mockImplementation(() => {
    return 'callback';
  });
  const program = new commander.Command();
  program.argument('<n...>', 'number', mockCoercion).action(() => {});
  program.parse(['1', '2'], { from: 'user' });
  expect(mockCoercion).toHaveBeenCalledTimes(2);
  expect(mockCoercion).toHaveBeenNthCalledWith(1, '1', undefined);
  expect(mockCoercion).toHaveBeenNthCalledWith(2, '2', 'callback');
});

test('when variadic argument without action handler then .processedArg has array', () => {
  const program = new commander.Command();
  program.argument('<n...>', 'number');
  program.parse(['1', '2'], { from: 'user' });
  expect(program.processedArgs).toEqual([['1', '2']]);
});

test('when parseFloat "1e2" then action argument is 100', () => {
  let actionValue;
  const program = new commander.Command();
  program.argument('<number>', 'float argument', parseFloat).action((arg) => {
    actionValue = arg;
  });
  program.parse(['1e2'], { from: 'user' });
  expect(actionValue).toEqual(100);
  expect(program.processedArgs).toEqual([actionValue]);
});

test('when defined default value for required argument then throw', () => {
  const program = new commander.Command();
  expect(() => {
    program.argument('<number>', 'float argument', 4);
  }).toThrow();
});

test('when custom processing for argument throws plain error then not CommanderError caught', () => {
  function justSayNo(value) {
    throw new Error('no no no');
  }
  const program = new commander.Command();
  program
    .exitOverride()
    .argument('[n]', 'number', justSayNo)
    .action(() => {});

  let caughtErr;
  try {
    program.parse(['green'], { from: 'user' });
  } catch (err) {
    caughtErr = err;
  }

  expect(caughtErr).toBeInstanceOf(Error);
  expect(caughtErr).not.toBeInstanceOf(commander.CommanderError);
});

tests/options.variadic.test.js.source:
const commander = require('../');

describe('variadic option with required value', () => {
  test('when variadic with value missing then error', () => {
    const program = new commander.Command();
    program
      .exitOverride()
      .configureOutput({ writeErr: jest.fn() })
      .option('-r,--required <value...>');

    expect(() => {
      program.parse(['--required'], { from: 'user' });
    }).toThrow();
  });

  test('when variadic with one value then set in array', () => {
    const program = new commander.Command();
    program.option('-r,--required <value...>');

    program.parse(['--required', 'one'], { from: 'user' });
    expect(program.opts().required).toEqual(['one']);
  });

  test('when variadic with two values then set in array', () => {
    const program = new commander.Command();
    program.option('-r,--required <value...>');

    program.parse(['--required', 'one', 'two'], { from: 'user' });
    expect(program.opts().required).toEqual(['one', 'two']);
  });

  test('when variadic with repeated values then set in array', () => {
    const program = new commander.Command();
    program.option('-r,--required <value...>');

    program.parse(['--required', 'one', '--required', 'two'], { from: 'user' });
    expect(program.opts().required).toEqual(['one', 'two']);
  });

  test('when variadic used with choices and one value then set in array', () => {
    const program = new commander.Command();
    program.addOption(
      new commander.Option('-r,--required <value...>').choices(['one', 'two']),
    );

    program.parse(['--required', 'one'], { from: 'user' });
    expect(program.opts().required).toEqual(['one']);
  });

  test('when variadic used with choices and two values then set in array', () => {
    const program = new commander.Command();
    program.addOption(
      new commander.Option('-r,--required <value...>').choices(['one', 'two']),
    );

    program.parse(['--required', 'one', 'two'], { from: 'user' });
    expect(program.opts().required).toEqual(['one', 'two']);
  });

  test('when variadic with short combined argument then not variadic', () => {
    const program = new commander.Command();
    program.option('-r,--required <value...>').argument('[arg]');

    program.parse(['-rone', 'operand'], { from: 'user' });
    expect(program.opts().required).toEqual(['one']);
  });

  test('when variadic with long combined argument then not variadic', () => {
    const program = new commander.Command();
    program.option('-r,--required <value...>').argument('[arg]');

    program.parse(['--required=one', 'operand'], { from: 'user' });
    expect(program.opts().required).toEqual(['one']);
  });

  test('when variadic with value followed by option then option not eaten', () => {
    const program = new commander.Command();
    program
      .option('-r,--required <value...>')
      .option('-f, --flag')
      .argument('[arg]');

    program.parse(['-r', 'one', '-f'], { from: 'user' });
    const opts = program.opts();
    expect(opts.required).toEqual(['one']);
    expect(opts.flag).toBe(true);
  });

  test('when variadic with no value and default then set to default', () => {
    const program = new commander.Command();
    program.option(
      '-r,--required <value...>',
      'variadic description',
      'default',
    );

    program.parse([], { from: 'user' });
    expect(program.opts().required).toEqual('default');
  });

  test('when variadic with coercion then coercion sets value', () => {
    const program = new commander.Command();
    program.option(
      '-r,--required <value...>',
      'variadic description',
      parseFloat,
    );

    // variadic processing reads the multiple values, but up to custom coercion what it does.
    program.parse(['--required', '1e2', '1e3'], { from: 'user' });
    expect(program.opts().required).toEqual(1000);
  });
});

// Not retesting everything, but do some tests on variadic with optional
describe('variadic option with optional value', () => {
  test('when variadic not specified then value undefined', () => {
    const program = new commander.Command();
    program.option('-o,--optional [value...]');

    program.parse([], { from: 'user' });
    expect(program.opts().optional).toBeUndefined();
  });

  test('when variadic used as boolean flag then value true', () => {
    const program = new commander.Command();
    program.option('-o,--optional [value...]');

    program.parse(['--optional'], { from: 'user' });
    expect(program.opts().optional).toBe(true);
  });

  test('when variadic with one value then set in array', () => {
    const program = new commander.Command();
    program.option('-o,--optional [value...]');

    program.parse(['--optional', 'one'], { from: 'user' });
    expect(program.opts().optional).toEqual(['one']);
  });

  test('when variadic with two values then set in array', () => {
    const program = new commander.Command();
    program.option('-o,--optional [value...]');

    program.parse(['--optional', 'one', 'two'], { from: 'user' });
    expect(program.opts().optional).toEqual(['one', 'two']);
  });
});

describe('variadic special cases', () => {
  test('when option flags has word character before dots then is variadic', () => {
    const program = new commander.Command();
    program.option('-c,--comma [value...]');

    expect(program.options[0].variadic).toBeTruthy();
  });

  test('when option flags has special characters before dots then not variadic', () => {
    // This might be used to describe coercion for comma separated values, and is not variadic.
    const program = new commander.Command();
    program.option('-c,--comma [value,...]');

    expect(program.options[0].variadic).toBeFalsy();
  });

  test('when option has default array then specified value is used instead of default (not appended)', () => {
    const program = new commander.Command();
    program.option('-c,--comma [value...]', 'values', ['default']);
    program.parse(['--comma', 'CCC'], { from: 'user' });

    expect(program.opts().comma).toEqual(['CCC']);
  });
});

