      .action(actionMock);
    expect(() => {
      program.parse(['node', 'test']);
    }).toThrow();
    expect(actionMock).not.toHaveBeenCalled();
  },
);

// Changes made in #729 to call program action handler
test('when .action on program and no arguments then action called', () => {
