  });

  test('when no extra arguments specified for command then variadic arg is empty array', () => {
    const actionMock = jest.fn();
    const program = new commander.Command();
    const cmd = program.command('sub [variadicArg...]').action(actionMock);

    program.parse(['node', 'test', 'sub']);

    expect(actionMock).toHaveBeenCalledWith([], cmd.opts(), cmd);
  });

  test('when extra arguments specified for command then variadic arg is array of values', () => {
    const actionMock = jest.fn();
    const program = new commander.Command();
    const cmd = program.command('sub [variadicArg...]').action(actionMock);
    const extraArguments = ['a', 'b', 'c'];

    program.parse(['node', 'test', 'sub', ...extraArguments]);

    expect(actionMock).toHaveBeenCalledWith(extraArguments, cmd.opts(), cmd);
  });

  test('when program variadic argument not last then error', () => {
    const program = new commander.Command();

    expect(() => {
      program.argument('<variadicArg...>').argument('[optionalArg]');
    }).toThrow("only the last argument can be variadic 'variadicArg'");
  });

  test('when command variadic argument not last then error', () => {
    const program = new commander.Command();

    expect(() => {
      program.command('sub <variadicArg...> [optionalArg]');
    }).toThrow("only the last argument can be variadic 'variadicArg'");
  });

  test('when variadic argument then usage shows variadic', () => {
    const program = new commander.Command();
    program.name('foo').argument('[args...]');

    expect(program.usage()).toBe('[options] [args...]');
  });

  test('when variadic used with choices and one value then set in array', () => {
    const program = new commander.Command();
    let passedArg;
    program
      .addArgument(new commander.Argument('<value...>').choices(['one', 'two']))
      .action((value) => {
        passedArg = value;
      });

    program.parse(['one'], { from: 'user' });
    expect(passedArg).toEqual(['one']);
  });

  test('when variadic used with choices and two values then set in array', () => {
    const program = new commander.Command();
    let passedArg;
    program
      .addArgument(new commander.Argument('<value...>').choices(['one', 'two']))
      .action((value) => {
        passedArg = value;
      });

    program.parse(['one', 'two'], { from: 'user' });
    expect(passedArg).toEqual(['one', 'two']);
  });

  test('when variadic has default array then specified value is used instead of default (not appended)', () => {
    const program = new commander.Command();
    let passedArg;
    program
      .addArgument(new commander.Argument('[value...]').default(['DEFAULT']))
      .action((value) => {
        passedArg = value;
      });

    program.parse(['one', 'two'], { from: 'user' });
    expect(passedArg).toEqual(['one', 'two']);
  });
});

tests/options.optsWithGlobals.test.js.source:
const commander = require('../');

// Testing optsWithGlobals and getOptionValueSourceWithGlobals with focus on globals.

describe('optsWithGlobals', () => {
  test('when variety of options used with program then opts is same as optsWithGlobals', () => {
    const program = new commander.Command();
    program
      .option('-b, --boolean')
      .option('-r, --require-value <value)')
      .option('-f, --float <value>', 'description', parseFloat)
      .option('-d, --default-value <value)', 'description', 'default value')
      .option('-n, --no-something');

    program.parse(['-b', '-r', 'req', '-f', '1e2'], { from: 'user' });
    expect(program.opts()).toEqual(program.optsWithGlobals());
  });

  test('when options in sub and program then optsWithGlobals includes both', () => {
    const program = new commander.Command();
    let mergedOptions;
    program.option('-g, --global <value>');
    program
      .command('sub')
      .option('-l, --local <value)')
      .action((options, cmd) => {
        mergedOptions = cmd.optsWithGlobals();
      });

    program.parse(['-g', 'GGG', 'sub', '-l', 'LLL'], { from: 'user' });
    expect(mergedOptions).toEqual({ global: 'GGG', local: 'LLL' });
  });

  test('when options in sub and subsub then optsWithGlobals includes both', () => {
    const program = new commander.Command();
    let mergedOptions;
    program
      .command('sub')
      .option('-g, --global <value)')
      .command('subsub')
      .option('-l, --local <value)')
      .action((options, cmd) => {
        mergedOptions = cmd.optsWithGlobals();
      });

    program.parse(['sub', '-g', 'GGG', 'subsub', '-l', 'LLL'], {
      from: 'user',
    });
    expect(mergedOptions).toEqual({ global: 'GGG', local: 'LLL' });
  });

  test('when same named option in sub and program then optsWithGlobals includes global', () => {
    const program = new commander.Command();
    let mergedOptions;
    program.option('-c, --common <value>').enablePositionalOptions();
    program
      .command('sub')
      .option('-c, --common <value)')
      .action((options, cmd) => {
        mergedOptions = cmd.optsWithGlobals();
      });

    program.parse(['-c', 'GGG', 'sub', '-c', 'LLL'], { from: 'user' });
    expect(mergedOptions).toEqual({ common: 'GGG' });
  });
});

describe('getOptionValueSourceWithGlobals', () => {
  test('when option used with simple command then source is defined', () => {
    const program = new commander.Command();
    program.option('-g, --global');

    program.parse(['-g'], { from: 'user' });
    expect(program.getOptionValueSourceWithGlobals('global')).toEqual('cli');
  });

  test('when option used with program then source is defined', () => {
    const program = new commander.Command();
    program.option('-g, --global');
    const sub = program
      .command('sub')
      .option('-l, --local')
      .action(() => {});

    program.parse(['sub', '-g'], { from: 'user' });
    expect(sub.getOptionValueSourceWithGlobals('global')).toEqual('cli');
  });

  test('when option used with subcommand then source is defined', () => {
    const program = new commander.Command();
    program.option('-g, --global');
    const sub = program
      .command('sub')
      .option('-l, --local')
      .action(() => {});

    program.parse(['sub', '-l'], { from: 'user' });
    expect(sub.getOptionValueSourceWithGlobals('local')).toEqual('cli');
  });

  test('when same named option in sub and program then source is defined by global', () => {
    const program = new commander.Command();
    program
      .enablePositionalOptions()
      .option('-c, --common <value>', 'description', 'default value');
    const sub = program
      .command('sub')
      .option('-c, --common <value>')
      .action(() => {});

    program.parse(['sub', '--common', 'value'], { from: 'user' });
    expect(sub.getOptionValueSourceWithGlobals('common')).toEqual('default');
  });
});

tests/command.showHelpAfterError.test.js.source:
const commander = require('../');

describe('showHelpAfterError with message', () => {
  const customHelpMessage = 'See --help';

  function makeProgram() {
    const writeMock = jest.fn();
    const program = new commander.Command();
    program
      .exitOverride()
      .showHelpAfterError(customHelpMessage)
      .configureOutput({ writeErr: writeMock });

    return { program, writeMock };
  }

  test('when missing command-argument then shows help', () => {
    const { program, writeMock } = makeProgram();
    program.argument('<file>');
    let caughtErr;
    try {
      program.parse([], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }
    expect(caughtErr.code).toBe('commander.missingArgument');
    expect(writeMock).toHaveBeenLastCalledWith(`${customHelpMessage}\n`);
  });

  test('when missing option-argument then shows help', () => {
    const { program, writeMock } = makeProgram();
    program.option('--output <file>');
    let caughtErr;
    try {
      program.parse(['--output'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }
    expect(caughtErr.code).toBe('commander.optionMissingArgument');
    expect(writeMock).toHaveBeenLastCalledWith(`${customHelpMessage}\n`);
  });

  test('when missing mandatory option then shows help', () => {
    const { program, writeMock } = makeProgram();
    program.requiredOption('--password <cipher>');
    let caughtErr;
    try {
      program.parse([], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }
    expect(caughtErr.code).toBe('commander.missingMandatoryOptionValue');
    expect(writeMock).toHaveBeenLastCalledWith(`${customHelpMessage}\n`);
  });

  test('when unknown option then shows help', () => {
    const { program, writeMock } = makeProgram();
    let caughtErr;
    try {
      program.parse(['--unknown-option'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }
    expect(caughtErr.code).toBe('commander.unknownOption');
    expect(writeMock).toHaveBeenLastCalledWith(`${customHelpMessage}\n`);
  });

  test('when too many command-arguments then shows help', () => {
    const { program, writeMock } = makeProgram();
    program.allowExcessArguments(false);
    let caughtErr;
    try {
      program.parse(['surprise'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }
    expect(caughtErr.code).toBe('commander.excessArguments');
    expect(writeMock).toHaveBeenLastCalledWith(`${customHelpMessage}\n`);
  });

  test('when unknown command then shows help', () => {
    const { program, writeMock } = makeProgram();
    program.command('sub1');
    let caughtErr;
    try {
      program.parse(['sub2'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }
    expect(caughtErr.code).toBe('commander.unknownCommand');
    expect(writeMock).toHaveBeenLastCalledWith(`${customHelpMessage}\n`);
  });

  test('when invalid option choice then shows help', () => {
    const { program, writeMock } = makeProgram();
    program.addOption(new commander.Option('--color').choices(['red', 'blue']));
    let caughtErr;
    try {
      program.parse(['--color', 'pink'], { from: 'user' });
    } catch (err) {
      caughtErr = err;
    }
    expect(caughtErr.code).toBe('commander.invalidArgument');
    expect(writeMock).toHaveBeenLastCalledWith(`${customHelpMessage}\n`);
  });
});

test('when showHelpAfterError() and error and then shows full help', () => {
  const writeMock = jest.fn();
  const program = new commander.Command();
  program
    .exitOverride()
    .showHelpAfterError()
    .configureOutput({ writeErr: writeMock });

  try {
    program.parse(['--unknown-option'], { from: 'user' });
  } catch (err) {
    /* empty */
  }
  expect(writeMock).toHaveBeenLastCalledWith(program.helpInformation());
});

