tests/command.showSuggestionAfterError.t.source:
const { Command } = require('../');

function getSuggestion(program, arg) {
  let message = '';
  program.exitOverride().configureOutput({
    writeErr: (str) => {
      message = str;
    },
  });

  try {
    program.parse([arg], { from: 'user' });
  } catch (err) {
    /* empty */
  }

  const match = message.match(/Did you mean (one of )?(.*)\?/);
  return match ? match[2] : null;
}

test('when unknown command and showSuggestionAfterError() then show suggestion', () => {
  const program = new Command();
  program.showSuggestionAfterError();
  program.command('example');
  const suggestion = getSuggestion(program, 'exampel');
  expect(suggestion).toBe('example');
});

test('when unknown command and showSuggestionAfterError(false) then do not show suggestion', () => {
  const program = new Command();
  program.showSuggestionAfterError(false);
  program.command('example');
  const suggestion = getSuggestion(program, 'exampel');
  expect(suggestion).toBe(null);
});

test('when unknown option and showSuggestionAfterError() then show suggestion', () => {
  const program = new Command();
  program.showSuggestionAfterError();
  program.option('--example');
  const suggestion = getSuggestion(program, '--exampel');
  expect(suggestion).toBe('--example');
});

test('when unknown option and showSuggestionAfterError(false) then do not show suggestion', () => {
  const program = new Command();
  program.showSuggestionAfterError(false);
  program.option('--example');
  const suggestion = getSuggestion(program, '--exampel');
  expect(suggestion).toBe(null);
});

tests/help.stripAnsi.test.js.source:
const { stripColor } = require('../lib/help');

// https://en.wikipedia.org/wiki/ANSI_escape_code#SGR_(Select_Graphic_Rendition)_parameters

// Test the lowest level routine being used for SGR support.

const ESC = '\u001b';
const CSI = ESC + '[';

test('SGR only', () => {
  const str = `${CSI}4m`;
  expect(stripColor(str)).toEqual('');
});

test('SGR at start', () => {
  const str = `${CSI}4mX`;
  expect(stripColor(str)).toEqual('X');
});

test('SGR in middle', () => {
  const str = `X${CSI}4mY`;
  expect(stripColor(str)).toEqual('XY');
});

test('SGR at end', () => {
  const str = `${CSI}4mY`;
  expect(stripColor(str)).toEqual('Y');
});

test('SGR pair', () => {
  // underline and not underlined
  const str = `${CSI}4mABC${CSI}24m`;
  expect(stripColor(str)).toEqual('ABC');
});

test('explicit reset with zero', () => {
  const str = `${CSI}0m`;
  expect(stripColor(str)).toEqual('');
});

test('implicit reset without zero', () => {
  const str = `${CSI}m`;
  expect(stripColor(str)).toEqual('');
});

test('multiple params: select foreground colour', () => {
  const str = `${CSI}38;5;4m`;
  expect(stripColor(str)).toEqual('');
});

test('multiple params: general', () => {
  const str = `${CSI}2;3;4m`;
  expect(stripColor(str)).toEqual('');
});

test('multiple params: missing param', () => {
  // CSI sequences can omit number (which is then treated as 0)
  const str = `${CSI};;m`;
  expect(stripColor(str)).toEqual('');
});

test('incomplete SGR sequence', () => {
  const str = `${CSI}14X`;
  expect(stripColor(str)).toEqual(`${CSI}14X`);
});

tests/help.minWidthToWrap.test.js.source:
const commander = require('../');

describe('Help.minWidthToWrap', () => {
  test('when enough width then wrap', () => {
    const program = new commander.Command('program');
    program.description('x '.repeat(110));
    program.configureOutput({
      getOutHelpWidth: () => 100, // lots of room to wrap
    });
    const helpText = program.helpInformation();
    const wrappedescription =
      'x '.repeat(50).trim() +
      '\n' +
      'x '.repeat(50).trim() +
      '\n' +
      'x '.repeat(10).trim();
    expect(helpText).toMatch(wrappedescription);
  });

  test('when not enough width then no wrap', () => {
    const program = new commander.Command('program');
    program.description('x '.repeat(50));
    program.configureOutput({
      getOutHelpWidth: () => 30, // too narrow to wrap
    });
    const helpText = program.helpInformation();
    const wrappedescription = 'x '.repeat(50);
    expect(helpText).toMatch(wrappedescription);
  });

  test('when make minWidthToWrap small then wrap', () => {
    const program = new commander.Command('program');
    program.description('x '.repeat(40));
    program.configureOutput({
      getOutHelpWidth: () => 30,
    });
    program.configureHelp({ minWidthToWrap: 20 });
    const helpText = program.helpInformation();
    const wrappedescription =
      'x '.repeat(15).trimEnd() +
      '\n' +
      'x '.repeat(15).trimEnd() +
      '\n' +
      'x '.repeat(10).trimEnd();
    expect(helpText).toMatch(wrappedescription);
  });
});

