tests/args.literal.test.js.source:
const commander = require('../');

// Utility Conventions: http://pubs.opengroup.org/onlinepubs/9699919799/basedefs/V1_chap12.html#tag_12_02
//
// 12.2 Utility Syntax Guidelines, Guideline 10:
// The first -- argument that is not an option-argument should be accepted as a delimiter indicating the end of options. Any following arguments should be treated as operands, even if they begin with the '-' character.

test('when arguments includes -- then stop processing options', () => {
  const program = new commander.Command();
  program
    .option('-f, --foo', 'add some foo')
    .option('-b, --bar', 'add some bar')
    .argument('[args...]');
  program.parse(['node', 'test', '--foo', '--', '--bar', 'baz']);
  // More than one assert, ported from legacy test
  const opts = program.opts();
  expect(opts.foo).toBe(true);
  expect(opts.bar).toBeUndefined();
  expect(program.args).toEqual(['--bar', 'baz']);
});

test('when arguments include -- then more literals are passed-through as args', () => {
  const program = new commander.Command();
  program
    .option('-f, --foo', 'add some foo')
    .option('-b, --bar', 'add some bar')
    .argument('[args...]');
  program.parse(['node', 'test', '--', 'cmd', '--', '--arg']);
  expect(program.args).toEqual(['cmd', '--', '--arg']);
});

tests/option.bad-flags.test.js.source:
const { Option } = require('../');

// Check that unsupported flags throw.
test.each([
  { flags: '-a, -b' }, // too many short flags
  { flags: '-a, -b <value>' },
  { flags: '-a, -b, --long' },
  { flags: '--one, --two, --three' }, // too many long flags
  { flags: '-ws' }, // short flag with more than one character
  { flags: '---triple' }, // double dash not followed by a non-dash
  { flags: 'sdkjhskjh' }, // oops, no flags
  { flags: '-a,-b' }, // try all the separators
  { flags: '-a|-b' },
  { flags: '-a -b' },
])('when construct Option with flags %p then throw', ({ flags }) => {
  expect(() => {
    new Option(flags);
  }).toThrow(/^option creation failed/);
});

// Check that supported flags do not throw.
test.each([
  { flags: '-s' }, // single short
  { flags: '--long' }, // single long
  { flags: '-b, --both' }, // short and long
  { flags: '--both, -b' }, // long and short
  { flags: '--ws, --workspace' }, // two long (morally shortish and long)
  { flags: '-b,--both <comma>' },
  { flags: '-b|--both <bar>' },
  { flags: '-b --both [space]' },
  { flags: '-v, --variadic <files...>' },
])('when construct Option with flags %p then do not throw', ({ flags }) => {
  expect(() => {
    new Option(flags);
  }).not.toThrow();
});

