tests/help.sortCommands.test.js.source:
const commander = require('../');

// These are tests of the Help class, not of the Command help.
// There is some overlap with the higher level Command tests (which predate Help).

describe('sortSubcommands', () => {
  test('when unsorted then commands in order added', () => {
    const program = new commander.Command();
    program
      .command('ccc', 'desc')
      .command('aaa', 'desc')
      .command('bbb', 'desc');
    const helper = program.createHelp();
    const visibleCommandNames = helper
      .visibleCommands(program)
      .map((cmd) => cmd.name());
    expect(visibleCommandNames).toEqual(['ccc', 'aaa', 'bbb', 'help']);
  });

  test('when sortSubcommands:true then commands sorted', () => {
    const program = new commander.Command();
    program
      .configureHelp({ sortSubcommands: true })
      .command('ccc', 'desc')
      .command('aaa', 'desc')
      .command('bbb', 'desc');
    const helper = program.createHelp();
    const visibleCommandNames = helper
      .visibleCommands(program)
      .map((cmd) => cmd.name());
    expect(visibleCommandNames).toEqual(['aaa', 'bbb', 'ccc', 'help']);
  });
});

tests/argument.required.test.js.source:
const commander = require('../');

// Low-level tests of setting Argument.required.
// Higher level tests of optional/required arguments elsewhere.

test('when name with surrounding <> then argument required', () => {
  const argument = new commander.Argument('<name>');
  expect(argument.required).toBe(true);
});

test('when name with surrounding [] then argument optional', () => {
  const argument = new commander.Argument('[name]');
  expect(argument.required).toBe(false);
});

test('when name without surrounding brackets then argument required', () => {
  // default behaviour, allowed from Commander 8
  const argument = new commander.Argument('name');
  expect(argument.required).toBe(true);
});

test('when call .argRequired() then argument required', () => {
  const argument = new commander.Argument('name');
  argument.required = false;
  argument.argRequired();
  expect(argument.required).toBe(true);
});

test('when call .argOptional() then argument optional', () => {
  const argument = new commander.Argument('name');
  argument.required = true;
  argument.argOptional();
  expect(argument.required).toBe(false);
});

tests/argument.chain.test.js.source:
const { Argument } = require('../');

describe('Argument methods that should return this for chaining', () => {
  test('when call .default() then returns this', () => {
    const argument = new Argument('<value>');
    const result = argument.default(3);
    expect(result).toBe(argument);
  });

  test('when call .argParser() then returns this', () => {
    const argument = new Argument('<value>');
    const result = argument.argParser(() => {});
    expect(result).toBe(argument);
  });

  test('when call .choices() then returns this', () => {
    const argument = new Argument('<value>');
    const result = argument.choices(['a']);
    expect(result).toBe(argument);
  });

  test('when call .argRequired() then returns this', () => {
    const argument = new Argument('<value>');
    const result = argument.argRequired();
    expect(result).toBe(argument);
  });

  test('when call .argOptional() then returns this', () => {
    const argument = new Argument('<value>');
    const result = argument.argOptional();
    expect(result).toBe(argument);
  });
});

tests/createCommand.test.js.source:
const commander = require('../');

test('when createCommand then unattached', () => {
  const program = new commander.Command();
  const cmd = program.createCommand();
  expect(program.commands.length).toBe(0);
  expect(cmd.parent).toBeFalsy(); // (actually null, but use weaker test for unattached)
});

test('when subclass overrides createCommand then subcommand is subclass', () => {
  class MyClass extends commander.Command {
    constructor(name) {
      super();
      this.myProperty = 'myClass';
    }

    createCommand(name) {
      return new MyClass(name);
    }
  }
  const program = new MyClass();
  const sub = program.command('sub');
  expect(sub.myProperty).toEqual('myClass');
});

test('when override createCommand then subcommand is custom', () => {
  function createCustom(name) {
    const cmd = new commander.Command();
    cmd.myProperty = 'custom';
    return cmd;
  }
  const program = createCustom();
  program.createCommand = createCustom;
  const sub = program.command('sub');
  expect(sub.myProperty).toEqual('custom');
});

